import React, { useState, useEffect, useRef } from 'react';
import { QRCodeCanvas } from 'qrcode.react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function QRManagement() {
  const [qrList, setQrList] = useState([]);
  const qrCanvasRefs = useRef({});  // Use a ref object to store QRCodeCanvas refs for each QR

  const navigate = useNavigate();

  // Fetch QR codes from backend
  useEffect(() => {
    const fetchQRCodes = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/get_qrs');
        setQrList(response.data.Qrs);
      } catch (error) {
        console.error('Error fetching QR codes:', error);
      }
    };
    fetchQRCodes();
  }, []);

  // Handle "Book Slot" button click to navigate to the booking page
  const handleBookSlot = () => {
    navigate('/slot_management');
  };

  // Handle QR code download
  const handleDownload = (qrId) => {
    const canvas = qrCanvasRefs.current[qrId]; // Access the specific canvas ref by qrId
    if (canvas) {
      const dataUrl = canvas.toDataURL('image/png'); // Get image data
      const link = document.createElement('a');
      link.href = dataUrl;
      link.download = `QR_Code_${qrId}.png`;
      link.click();
    } else {
      console.error('Canvas not found for QR ID:', qrId);
    }
  };

  return (
    <div className="page-content">
      {/* start page title */}
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>QR Management</h4>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* end page title */}

      <div className="container-fluid">
        <div className="page-content-wrapper">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <div className="p-4" style={{ minHeight: 300 }}>
                    <h5>QR Code Management</h5>

                    {/* Render dynamic QR code list */}
                    <div className="row mt-4">
                      {qrList.map((qr) => (
                        <div key={qr.qrCodeId} className="col-md-4 text-center mb-3">
                          <QRCodeCanvas
                            value={qr.url}
                            size={100}
                            ref={(el) => (qrCanvasRefs.current[qr.qrCodeId] = el)}  // Set ref for each QR code
                          />
                          <p className="mt-2">QR - {qr.qrCodeId}</p>

                          {/* Book Slot Button */}
                          <button
                            className="btn btn-primary"
                            onClick={handleBookSlot}
                          >
                            Book Slot
                          </button>

                          {/* Download Button */}
                          <button
                            className="btn btn-outline-success waves-effect waves-light"
                            onClick={() => handleDownload(qr.qrCodeId)}
                          >
                            Download QR
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </div> {/* end col */}
          </div> {/* end row */}
        </div>
      </div> {/* container-fluid */}
    </div>
  );
}

export default QRManagement;
