import React, { useState } from 'react';
import './LandingPageBuilder.css';  // You can put the relevant styles in a separate CSS file

const LandingPageBuilder = () => {
  const [elements, setElements] = useState([]);

  const addElement = (type) => {
    let newElement;
    switch (type) {
      case 'text':
        newElement = { id: Date.now(), type: 'text', content: 'Edit your text here' };
        break;
      case 'button':
        newElement = { id: Date.now(), type: 'button', content: 'Click Me' };
        break;
      case 'image':
        newElement = { id: Date.now(), type: 'image', src: 'https://via.placeholder.com/150' };
        break;
      case 'video':
        newElement = { id: Date.now(), type: 'video', src: 'https://www.youtube.com/embed/dQw4w9WgXcQ' };
        break;
      default:
        return;
    }
    setElements([...elements, newElement]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const type = e.dataTransfer.getData('type');
    if (type) {
      setElements((prevElements) => [
        ...prevElements,
        { id: Date.now(), type, content: 'Dropped Element' }
      ]);
    }
  };

  return (
    <div className="landing-page-builder">
      {/* Header Section */}
      <header className="header">
        <h1>Landing Page Builder</h1>
      </header>

      <div className="builder-container">
        {/* Toolbox */}
        <div className="toolbox">
          <div className="toolbox-item" onDragStart={() => addElement('text')} draggable>
            Text
          </div>
          <div className="toolbox-item" onDragStart={() => addElement('button')} draggable>
            Button
          </div>
          <div className="toolbox-item" onDragStart={() => addElement('image')} draggable>
            Image
          </div>
          <div className="toolbox-item" onDragStart={() => addElement('video')} draggable>
            Video
          </div>
        </div>

        {/* Dropzone */}
        <div
          className="builder-dropzone"
          onDrop={handleDrop}
          onDragOver={(e) => e.preventDefault()}
        >
          {elements.map((element) => {
            switch (element.type) {
              case 'text':
                return (
                  <div key={element.id} className="text-item" contentEditable>
                    {element.content}
                  </div>
                );
              case 'button':
                return <button key={element.id}>{element.content}</button>;
              case 'image':
                return <img key={element.id} src={element.src} alt="Dropped" />;
              case 'video':
                return <iframe key={element.id} src={element.src} width="300" height="200" title="video"></iframe>;
              default:
                return null;
            }
          })}
        </div>
      </div>

      {/* Footer Section */}
      <footer className="footer">
        <p>&copy; 2024 Landing Page Builder</p>
      </footer>
    </div>
  );
};

export default LandingPageBuilder;
