import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState(''); // State to store the file URL
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [headings, setHeadings] = useState([{
    title: '',
    description: '',
    link: '',
    image: null,
  }]);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Fetch the file URL from the backend API
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId'); // Get templateId from URL
    if (templateId) {
      const fetchFileUrl = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          const data = await response.json();
          if (data.fileUrl) {
            setFileUrl(data.fileUrl); // Set the file URL state
          }
        } catch (error) {
          console.error('Error fetching file URL:', error);
        }
      };
      fetchFileUrl();
    }
  }, []);

  const handleAddHeading = () => {
    setHeadings([
      ...headings,
      { title: '', description: '', link: '', image: null }
    ]);
  };

  const handleRemoveHeading = (index) => {
    const newHeadings = headings.filter((_, i) => i !== index);
    setHeadings(newHeadings);
  };

  const handleHeadingChange = (index, e) => {
    const { name, value } = e.target;
    const updatedHeadings = [...headings];
    updatedHeadings[index][name] = value;
    setHeadings(updatedHeadings);
  };

  const handleImageChange = (index, e) => {
    const updatedHeadings = [...headings];
    updatedHeadings[index].image = e.target.files[0];
    setHeadings(updatedHeadings);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);

    // Add all headings, descriptions, links, and images
    headings.forEach((heading, index) => {
      formData.append(`title${index + 1}`, heading.title);
      formData.append(`description${index + 1}`, heading.description);
      formData.append(`link${index + 1}`, heading.link);
      formData.append(`image${index + 1}`, heading.image);
    });

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Upload Logo */}
                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Brand Description */}
                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dynamic Headings */}
                  {headings.map((heading, index) => (
                    <div key={index}>
                      {/* Title */}
                      <div className="mb-3">
                        <label htmlFor={`title${index}`} className="form-label">Title {index + 1}</label>
                        <input
                          type="text"
                          name="title"
                          id={`title${index}`}
                          className="form-control"
                          value={heading.title}
                          onChange={(e) => handleHeadingChange(index, e)}
                          required
                        />
                      </div>

                      {/* Description */}
                      <div className="mb-3">
                        <label htmlFor={`description${index}`} className="form-label">Description {index + 1}</label>
                        <textarea
                          name="description"
                          id={`description${index}`}
                          className="form-control"
                          rows="3"
                          value={heading.description}
                          onChange={(e) => handleHeadingChange(index, e)}
                          required
                        />
                      </div>

                      {/* Link */}
                      <div className="mb-3">
                        <label htmlFor={`link${index}`} className="form-label">Link {index + 1}</label>
                        <input
                          type="url"
                          name="link"
                          id={`link${index}`}
                          className="form-control"
                          value={heading.link}
                          onChange={(e) => handleHeadingChange(index, e)}
                        />
                      </div>

                      {/* Image Upload */}
                      <div className="mb-3">
                        <label htmlFor={`image${index}`} className="form-label">Upload Image {index + 1}</label>
                        <input
                          type="file"
                          name="image"
                          id={`image${index}`}
                          className="form-control"
                          onChange={(e) => handleImageChange(index, e)}
                        />
                      </div>

                      {/* Remove Heading Button */}
                      {headings.length > 1 && (
                        <button
                          type="button"
                          onClick={() => handleRemoveHeading(index)}
                          className="btn btn-danger mb-3"
                        >
                          Remove Heading {index + 1}
                        </button>
                      )}
                    </div>
                  ))}

                  {/* Add New Heading Button */}
                  <button
                    type="button"
                    onClick={handleAddHeading}
                    className="btn btn-success mb-3"
                  >
                    Add New Heading
                  </button>

                  {/* Success Message */}
                  {showSuccessMessage && (
                    <div className="alert alert-success mb-3" role="alert">
                      Data successfully added!
                    </div>
                  )}

                  <button type="submit" className="btn btn-primary">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Sticky Mobile Frame with HTML Content */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px', // Keep it 20px from the top
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '350px', // Adjusted width for the mobile frame
                height: '650px', // Adjusted height
                border: '16px solid #e7e7e7',
                borderRadius: '20px',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {fileUrl && (
                <iframe
                  src={fileUrl}
                  width="100%"
                  height="100%"
                  style={{
                    border: 'none',
                    borderRadius: '20px',
                  }}
                  title="Mobile Template"
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
