import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { QRCodeCanvas } from 'qrcode.react'; // For rendering QR codes

function QRManagement() {
  const qrUrl = 'https://www.google.com'; // URL to encode in QR code

  // Handle downloading the QR code
  const handleDownload = () => {
    const canvas = document.getElementById('qr-google'); // ID for the QR code canvas
    const link = document.createElement('a');
    link.href = canvas.toDataURL('image/png'); // Get the image URL from the canvas
    link.download = 'Google_QR_Code.png'; // Set the filename for the download
    link.click(); // Trigger the download
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">QR Management</h2>
      <div className="card mt-4">
        <div className="card-body text-center">
          <h4>QR Code for Google</h4>

          {/* QR Code Canvas */}
          <QRCodeCanvas
            id="qr-google" // Assigning ID to canvas for download
            value={qrUrl}
            size={200}
            bgColor={"#ffffff"}
            fgColor={"#000000"}
            level={"H"}
          />

          <p className="mt-3">Scan to visit: <a href={qrUrl} target="_blank" rel="noopener noreferrer">{qrUrl}</a></p>

          {/* Download QR Button */}
          <button className="btn btn-primary mt-3" onClick={handleDownload}>
            Download QR Code
          </button>
        </div>
      </div>
    </div>
  );
}

export default QRManagement;
