import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState(''); // State to store the file URL
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [title1, setTitle1] = useState('');
  const [description1, setDescription1] = useState('');
  const [image1, setImage1] = useState(null);
  const [title2, setTitle2] = useState('');
  const [description2, setDescription2] = useState('');
  const [image2, setImage2] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Fetch the file URL from the backend API
  useEffect(() => {
    const fetchFileUrl = async () => {
      const fileId = 'your-file-id'; // Replace with dynamic file_id from the URL
      try {
        const response = await fetch(`http://localhost:7000/api/get-file/${fileId}`);
        const data = await response.json();
        if (data.fileUrl) {
          setFileUrl(data.fileUrl); // Set the file URL state
        }
      } catch (error) {
        console.error('Error fetching file URL:', error);
      }
    };

    fetchFileUrl(); // Fetch the file URL when the component mounts
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);
    formData.append('title1', title1);
    formData.append('description1', description1);
    formData.append('image1', image1);
    formData.append('title2', title2);
    formData.append('description2', description2);
    formData.append('image2', image2);

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Upload Logo */}
                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Brand Description */}
                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Title 1 */}
                  <div className="mb-3">
                    <label htmlFor="title1" className="form-label">Title 1</label>
                    <input
                      type="text"
                      id="title1"
                      className="form-control"
                      value={title1}
                      onChange={(e) => setTitle1(e.target.value)}
                      required
                    />
                  </div>

                  {/* Description 1 */}
                  <div className="mb-3">
                    <label htmlFor="description1" className="form-label">Description 1</label>
                    <textarea
                      id="description1"
                      className="form-control"
                      rows="3"
                      value={description1}
                      onChange={(e) => setDescription1(e.target.value)}
                      required
                    />
                  </div>

                  {/* Image Upload 1 */}
                  <div className="mb-3">
                    <label htmlFor="image1" className="form-label">Upload Image 1</label>
                    <input
                      type="file"
                      id="image1"
                      className="form-control"
                      onChange={(e) => setImage1(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Title 2 */}
                  <div className="mb-3">
                    <label htmlFor="title2" className="form-label">Title 2</label>
                    <input
                      type="text"
                      id="title2"
                      className="form-control"
                      value={title2}
                      onChange={(e) => setTitle2(e.target.value)}
                      required
                    />
                  </div>

                  {/* Description 2 */}
                  <div className="mb-3">
                    <label htmlFor="description2" className="form-label">Description 2</label>
                    <textarea
                      id="description2"
                      className="form-control"
                      rows="3"
                      value={description2}
                      onChange={(e) => setDescription2(e.target.value)}
                      required
                    />
                  </div>

                  {/* Image Upload 2 */}
                  <div className="mb-3">
                    <label htmlFor="image2" className="form-label">Upload Image 2</label>
                    <input
                      type="file"
                      id="image2"
                      className="form-control"
                      onChange={(e) => setImage2(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Success Message */}
                  {showSuccessMessage && (
                    <div className="alert alert-success mb-3" role="alert">
                      Data successfully added!
                    </div>
                  )}

                  <button type="submit" className="btn btn-primary">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Sticky Mobile Frame with HTML Content */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px', // Keep it 20px from the top
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '350px', // Adjusted width for the mobile frame
                height: '650px', // Adjusted height
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                overflow: 'hidden',
                boxShadow: '0 0 20px rgba(0, 0, 0, 0.1)',
                backgroundColor: '#fff',
                marginLeft: 'auto',
                marginRight: 'auto',
              }}
            >
              {/* Render HTML content from the fetched file URL */}
              {fileUrl && (
                <iframe
                  src={fileUrl} // Use the URL from API response
                  width="100%"
                  height="100%"
                  style={{
                    border: 'none',
                    borderRadius: '20px',
                  }}
                  title="Mobile Template"
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
