import React, { useState, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');

  // State to manage dynamic fields for titles, descriptions, links, and images
  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Fetch the file URL from the backend API
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId');
    if (templateId) {
      const fetchFileUrl = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          const data = await response.json();
          if (data.fileUrl) {
            setFileUrl(data.fileUrl);
          }
        } catch (error) {
          console.error('Error fetching file URL:', error);
        }
      };
      fetchFileUrl();
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);

    // Append dynamic fields for titles, descriptions, links, and images
    titles.forEach((title, index) => formData.append(`titles[${index}]`, title));
    descriptions.forEach((description, index) => formData.append(`descriptions[${index}]`, description));
    links.forEach((link, index) => formData.append(`links[${index}]`, link));
    images.forEach((image, index) => {
      if (image) formData.append(`images[${index}]`, image);
    });

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  // Add Field Handlers
  const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleChange = (index, field, value) => {
    switch (field) {
      case 'title':
        const newTitles = [...titles];
        newTitles[index] = value;
        setTitles(newTitles);
        break;
      case 'description':
        const newDescriptions = [...descriptions];
        newDescriptions[index] = value;
        setDescriptions(newDescriptions);
        break;
      case 'link':
        const newLinks = [...links];
        newLinks[index] = value;
        setLinks(newLinks);
        break;
      default:
        break;
    }
  };

  const handleImageChange = (index, file) => {
    const newImages = [...images];
    newImages[index] = file;
    setImages(newImages);
  };

  // Handle Removal of Fields
  const handleRemove = (index, field) => {
    switch (field) {
      case 'title':
        setTitles(titles.filter((_, i) => i !== index));
        break;
      case 'description':
        setDescriptions(descriptions.filter((_, i) => i !== index));
        break;
      case 'link':
        setLinks(links.filter((_, i) => i !== index));
        break;
      case 'image':
        setImages(images.filter((_, i) => i !== index));
        break;
      default:
        break;
    }
  };

  // Drag-and-Drop Setup
  const moveItem = (dragIndex, hoverIndex, type) => {
    const dragItem = type === 'title' ? titles[dragIndex] : type === 'description' ? descriptions[dragIndex] : type === 'link' ? links[dragIndex] : images[dragIndex];

    const updatedItems = type === 'title' ? [...titles] : type === 'description' ? [...descriptions] : type === 'link' ? [...links] : [...images];
    updatedItems.splice(dragIndex, 1);
    updatedItems.splice(hoverIndex, 0, dragItem);

    if (type === 'title') setTitles(updatedItems);
    else if (type === 'description') setDescriptions(updatedItems);
    else if (type === 'link') setLinks(updatedItems);
    else setImages(updatedItems);
  };

  const DraggableItem = ({ index, moveItem, type, children }) => {
    const [, drag] = useDrag({
      type: type,
      item: { index },
    });

    const [, drop] = useDrop({
      accept: type,
      hover: (item) => {
        if (item.index !== index) {
          moveItem(item.index, index, type);
          item.index = index;
        }
      },
    });

    return (
      <div ref={(node) => drag(drop(node))} style={{ padding: '8px', border: '1px solid #ddd', marginBottom: '8px' }}>
        {children}
      </div>
    );
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Upload Logo */}
                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Brand Description */}
                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Add Title Fields */}
                  {titles.map((title, index) => (
                    <DraggableItem key={index} index={index} moveItem={moveItem} type="title">
                      <div className="mb-3">
                        <label htmlFor={`title-${index}`} className="form-label">Title {index + 1}</label>
                        <input
                          type="text"
                          id={`title-${index}`}
                          className="form-control"
                          value={title}
                          onChange={(e) => handleChange(index, 'title', e.target.value)}
                        />
                        <div className="d-flex justify-content-between mt-2">
                          <button
                            type="button"
                            className="btn btn-danger waves-effect waves-light mb-3"
                            onClick={() => handleRemove(index, 'title')}
                          >
                            Remove Title
                          </button>
                        </div>
                      </div>
                    </DraggableItem>
                  ))}
                  <button
                    type="button"
                    className="btn btn-primary waves-effect waves-light mb-3"
                    onClick={handleAddTitle}
                  >
                    Add Title
                  </button>

                  {/* Add Description Fields */}
                  {descriptions.map((description, index) => (
                    <DraggableItem key={index} index={index} moveItem={moveItem} type="description">
                      <div className="mb-3">
                        <label htmlFor={`description-${index}`} className="form-label">Description {index + 1}</label>
                        <textarea
                          id={`description-${index}`}
                          className="form-control"
                          value={description}
                          onChange={(e) => handleChange(index, 'description', e.target.value)}
                        />
                        <div className="d-flex justify-content-between mt-2">
                          <button
                            type="button"
                            className="btn btn-danger waves-effect waves-light mb-3"
                            onClick={() => handleRemove(index, 'description')}
                          >
                            Remove Description
                          </button>
                        </div>
                      </div>
                    </DraggableItem>
                  ))}
                  <button
                    type="button"
                    className="btn btn-primary waves-effect waves-light mb-3"
                    onClick={handleAddDescription}
                  >
                    Add Description
                  </button>

                  {/* Add Link Fields */}
                  {links.map((link, index) => (
                    <DraggableItem key={index} index={index} moveItem={moveItem} type="link">
                      <div key={index} className="mb-3">
                        <label htmlFor={`link-${index}`} className="form-label">Link {index + 1}</label>
                        <input
                          type="text"
                          id={`link-${index}`}
                          className="form-control"
                          value={link}
                          onChange={(e) => handleChange(index, 'link', e.target.value)}
                        />
                        <div className="d-flex justify-content-between mt-2">
                          <button
                            type="button"
                            className="btn btn-danger waves-effect waves-light mb-3"
                            onClick={() => handleRemove(index, 'link')}
                          >
                            Remove Link
                          </button>
                        </div>
                      </div>
                    </DraggableItem>
                  ))}
                  <button
                    type="button"
                    className="btn btn-primary waves-effect waves-light mb-3"
                    onClick={handleAddLink}
                  >
                    Add Link
                  </button>

                  {/* Add Image Fields */}
                  {images.map((image, index) => (
                    <DraggableItem key={index} index={index} moveItem={moveItem} type="image">
                      <div key={index} className="mb-3">
                        <label htmlFor={`image-${index}`} className="form-label">Image {index + 1}</label>
                        <input
                          type="file"
                          id={`image-${index}`}
                          className="form-control"
                          onChange={(e) => handleImageChange(index, e.target.files[0])}
                        />
                        <div className="d-flex justify-content-between mt-2">
                          <button
                            type="button"
                            className="btn btn-danger waves-effect waves-light mb-3"
                            onClick={() => handleRemove(index, 'image')}
                          >
                            Remove Image
                          </button>
                        </div>
                      </div>
                    </DraggableItem>
                  ))}
                  <button
                    type="button"
                    className="btn btn-primary waves-effect waves-light mb-3"
                    onClick={handleAddImage}
                  >
                    Add Image
                  </button>

                  {/* Success Message */}
                  {showSuccessMessage && (
                    <div className="alert alert-success mb-3" role="alert">
                      Data successfully added!
                    </div>
                  )}

                  {/* Submit Button */}
                  <button type="submit" className="btn btn-success waves-effect waves-light mb-3">
                    Submit
                  </button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Sticky Mobile Frame with HTML Content */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px',
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '350px',
                height: '650px',
                border: '16px solid #e7e7e7',
                borderRadius: '20px',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {fileUrl && (
                <iframe
                  src={fileUrl}
                  width="100%"
                  height="100%"
                  style={{
                    border: 'none',
                    borderRadius: '20px',
                  }}
                  title="Mobile Template"
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
