import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId');
    if (templateId) {
      const fetchFileUrl = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          const data = await response.json();
          if (data.fileUrl) {
            setFileUrl(data.fileUrl);
          }
        } catch (error) {
          console.error('Error fetching file URL:', error);
        }
      };
      fetchFileUrl();
    }
  }, []);

  const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleRemove = (index, setFunction, array) => {
    const updatedArray = [...array];
    updatedArray.splice(index, 1);
    setFunction(updatedArray);
  };

  const handleChange = (e, index, setFunction, array) => {
    const updatedArray = [...array];
    updatedArray[index] = e.target.value;
    setFunction(updatedArray);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);

    titles.forEach((title, index) => formData.append(`titles[${index}]`, title));
    descriptions.forEach((description, index) =>
      formData.append(`descriptions[${index}]`, description)
    );
    links.forEach((link, index) => formData.append(`links[${index}]`, link));
    images.forEach((image, index) => {
      if (image) formData.append(`images[${index}]`, image);
    });

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.success) {
        alert('Data saved successfully!');
      } else {
        alert('Error saving data.');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="container">
        <h2>Brand Information</h2>

        {/* Add Buttons */}
        <div className="add-buttons mb-4">
          <button className="btn btn-primary me-2" onClick={handleAddTitle}>
            Add Title
          </button>
          <button className="btn btn-primary me-2" onClick={handleAddDescription}>
            Add Description
          </button>
          <button className="btn btn-primary me-2" onClick={handleAddLink}>
            Add Link
          </button>
          <button className="btn btn-primary" onClick={handleAddImage}>
            Add Image
          </button>
        </div>

        {/* Form Content */}
        <form onSubmit={handleSubmit}>
          {/* Upload Logo */}
          <div className="mb-3">
            <label htmlFor="logo" className="form-label">
              Upload Logo
            </label>
            <input
              type="file"
              id="logo"
              className="form-control"
              onChange={(e) => setLogo(e.target.files[0])}
            />
          </div>

          {/* Brand Name */}
          <div className="mb-3">
            <label htmlFor="brandName" className="form-label">
              Brand Name
            </label>
            <input
              type="text"
              id="brandName"
              className="form-control"
              value={brandName}
              onChange={(e) => setBrandName(e.target.value)}
              required
            />
          </div>

          {/* Brand Description */}
          <div className="mb-3">
            <label htmlFor="brandDescription" className="form-label">
              Brand Description
            </label>
            <textarea
              id="brandDescription"
              className="form-control"
              value={brandDescription}
              onChange={(e) => setBrandDescription(e.target.value)}
              rows="3"
              required
            />
          </div>

          {/* Dynamic Sections */}
          <div>
            {titles.map((title, index) => (
              <div key={index} className="mb-3">
                <label htmlFor={`title-${index}`} className="form-label">
                  Title {index + 1}
                </label>
                <div className="input-group">
                  <input
                    type="text"
                    id={`title-${index}`}
                    className="form-control"
                    value={title}
                    onChange={(e) => handleChange(e, index, setTitles, titles)}
                  />
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => handleRemove(index, setTitles, titles)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}

            {descriptions.map((description, index) => (
              <div key={index} className="mb-3">
                <label htmlFor={`description-${index}`} className="form-label">
                  Description {index + 1}
                </label>
                <div className="input-group">
                  <textarea
                    id={`description-${index}`}
                    className="form-control"
                    value={description}
                    onChange={(e) => handleChange(e, index, setDescriptions, descriptions)}
                    rows="2"
                  />
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => handleRemove(index, setDescriptions, descriptions)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}

            {links.map((link, index) => (
              <div key={index} className="mb-3">
                <label htmlFor={`link-${index}`} className="form-label">
                  Link {index + 1}
                </label>
                <div className="input-group">
                  <input
                    type="text"
                    id={`link-${index}`}
                    className="form-control"
                    value={link}
                    onChange={(e) => handleChange(e, index, setLinks, links)}
                  />
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => handleRemove(index, setLinks, links)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}

            {images.map((image, index) => (
              <div key={index} className="mb-3">
                <label htmlFor={`image-${index}`} className="form-label">
                  Image {index + 1}
                </label>
                <div className="input-group">
                  <input
                    type="file"
                    id={`image-${index}`}
                    className="form-control"
                    onChange={(e) => {
                      const updatedImages = [...images];
                      updatedImages[index] = e.target.files[0];
                      setImages(updatedImages);
                    }}
                  />
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => handleRemove(index, setImages, images)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>

          <button type="submit" className="btn btn-success">
            Submit
          </button>
        </form>
      </div>
    </DndProvider>
  );
};

export default EditTemplate;
