import { useState } from "react";
import { QRCodeCanvas } from "qrcode.react";

function QRCodeDesign() {
  const [qrData, setQrData] = useState(""); // QR Code Data
  const [selectedTemplate, setSelectedTemplate] = useState("template1"); // Template selection
  const [errorMessage, setErrorMessage] = useState(""); // Error messages

  // Template configurations
  const templates = {
    template1: {
      name: "Business Card",
      bgImage: "/path-to-business-card-template.jpg",
      qrPosition: { top: "70%", left: "50%" },
    },
    template2: {
      name: "Flyer",
      bgImage: "/path-to-flyer-template.jpg",
      qrPosition: { top: "60%", left: "40%" },
    },
    template3: {
      name: "Social Media",
      bgImage: "/path-to-social-media-template.jpg",
      qrPosition: { top: "50%", left: "50%" },
    },
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!qrData) {
      setErrorMessage("Please enter QR code data");
      return;
    }
    setErrorMessage(""); // Clear error if QR data is valid
  };

  return (
    <>
      <div className="page-content">
        <div className="page-title-box">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col-sm-6">
                <div className="page-title">
                  <h4>Generate QR Code</h4>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="container-fluid">
          <div className="page-content-wrapper">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-body">
                    <h4 className="header-title">QR Code Information</h4>
                    <p className="card-title-desc">Enter data to generate a QR code</p>

                    {/* Error Message */}
                    {errorMessage && (
                      <div className="alert alert-danger" role="alert">
                        {errorMessage}
                      </div>
                    )}

                    <form onSubmit={handleSubmit}>
                      {/* QR Code Data */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="qrData">
                          QR Code Data
                        </label>
                        <input
                          id="qrData"
                          name="qrData"
                          type="text"
                          className="form-control"
                          placeholder="Enter data for QR code"
                          value={qrData}
                          onChange={(e) => setQrData(e.target.value)}
                          required
                        />
                      </div>

                      {/* Template Selection */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="template">
                          Select Design Template
                        </label>
                        <select
                          id="template"
                          name="template"
                          className="form-control"
                          value={selectedTemplate}
                          onChange={(e) => setSelectedTemplate(e.target.value)}
                        >
                          {Object.keys(templates).map((templateKey) => (
                            <option key={templateKey} value={templateKey}>
                              {templates[templateKey].name}
                            </option>
                          ))}
                        </select>
                      </div>

                      <button type="submit" className="btn btn-primary">
                        Generate QR Code
                      </button>
                    </form>

                    {/* Template Preview */}
                    <div className="template-preview mt-4">
                      <h5>QR Code Template Preview</h5>
                      <div className="template-images d-flex justify-content-start">
                        {Object.keys(templates).map((template) => (
                          <div
                            key={template}
                            className="template-option p-2"
                            style={{ textAlign: "center" }}
                          >
                            <input
                              type="radio"
                              id={template}
                              name="template"
                              value={template}
                              checked={selectedTemplate === template}
                              onChange={() => setSelectedTemplate(template)}
                            />
                            <label htmlFor={template}>
                              <div
                                style={{
                                  width: "100px",
                                  height: "150px",
                                  backgroundImage: `url(${templates[template].bgImage})`,
                                  backgroundSize: "cover",
                                  border: "2px solid #ccc",
                                  margin: "10px auto",
                                  position: "relative",
                                }}
                              >
                                <p
                                  style={{
                                    position: "absolute",
                                    bottom: "5px",
                                    left: "50%",
                                    transform: "translateX(-50%)",
                                    color: "#fff",
                                    fontWeight: "bold",
                                    textShadow: "0 0 5px black",
                                  }}
                                >
                                  {templates[template].name}
                                </p>
                              </div>
                            </label>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* QR Code Preview */}
            {qrData && (
              <div className="mt-4">
                <h4>Generated QR Code</h4>
                <div
                  className="qr-template-preview"
                  style={{
                    position: "relative",
                    backgroundImage: `url(${templates[selectedTemplate].bgImage})`,
                    width: "500px",
                    height: "700px",
                    backgroundSize: "cover",
                    margin: "0 auto",
                    border: "1px solid #ccc",
                  }}
                >
                  {/* QR Code Overlay */}
                  <div
                    style={{
                      position: "absolute",
                      top: templates[selectedTemplate].qrPosition.top,
                      left: templates[selectedTemplate].qrPosition.left,
                      transform: "translate(-50%, -50%)",
                    }}
                  >
                    <QRCodeCanvas
                      value={qrData}
                      size={150}
                      bgColor="white"
                      fgColor="black"
                      level="L"
                    />
                  </div>
                </div>

                <a href={qrData} download="generated-qr-code.png">
                  <button className="btn btn-success mt-3">Download QR Code</button>
                </a>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default QRCodeDesign;
