// QREditor.js
import React, { useState } from 'react';
import { QRCodeCanvas } from 'qrcode.react';
import 'bootstrap/dist/css/bootstrap.min.css';

function QREditor({ qrCodeUrl, qrId }) {
  const [fgColor, setFgColor] = useState("#000000"); // Foreground color for the barcode
  const [qrSize, setQrSize] = useState(150); // QR Code size

  // Handle foreground color change (barcode color)
  const handleFgColorChange = (e) => setFgColor(e.target.value);

  // Handle QR code size change
  const handleSizeChange = (e) => setQrSize(parseInt(e.target.value));

  // Handle download functionality
  const handleDownload = () => {
    const canvas = document.getElementById(`custom-qr-${qrId}`);
    const link = document.createElement("a");
    link.href = canvas.toDataURL("image/png");
    link.download = `Customized_QR_${qrId}.png`;
    link.click();
  };

  // Template styles with background colors and text
  const templates = [
    { bgColor: '#ffff00', text: 'Scan me', textColor: '#000000' },  // Yellow with black text
    { bgColor: '#00f0f0', text: 'Scan me now!', textColor: '#ffffff' }, // Aqua with white text
    { bgColor: '#f0f0f0', text: 'Scan for offer!', textColor: '#0000ff' }, // Light grey with blue text
    { bgColor: '#98fb98', text: 'Get Started!', textColor: '#000000' }, // Pale green with black text
  ];

  return (
    <div className="card mt-3">
      <div className="card-body">
        <h5 className="card-title">Edit QR Code for ID: {qrId}</h5>

        {/* QR Code Templates */}
        <div className="row">
          {templates.map((template, index) => (
            <div key={index} className="col-md-3 mb-3">
              {/* Template Display */}
              <div
                style={{
                  backgroundColor: template.bgColor,
                  padding: '30px',
                  borderRadius: '10px',
                  position: 'relative',
                  textAlign: 'center',
                }}
              >
                {/* Text label inside the template */}
                <div
                  style={{
                    position: 'absolute',
                    top: '10px',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    color: template.textColor,
                    fontWeight: 'bold',
                    fontSize: '18px',
                  }}
                >
                  {template.text}
                </div>

                {/* QR Code Canvas with Border */}
                <div style={{ border: '5px solid #000', display: 'inline-block' }}>
                  <QRCodeCanvas
                    id={`custom-qr-${qrId}`}
                    value={qrCodeUrl}
                    size={qrSize}
                    bgColor="#ffffff" // Keep the background color white
                    fgColor={fgColor} // Foreground color (barcode color)
                    level={"H"} // Error correction level
                  />
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Customization Controls */}
        <div className="row mt-3">
          <div className="col-md-4">
            <label>Barcode Color:</label>
            <input
              type="color"
              value={fgColor}
              onChange={handleFgColorChange}
              className="form-control"
            />
          </div>
          <div className="col-md-4">
            <label>QR Code Size:</label>
            <input
              type="number"
              min="100"
              max="300"
              value={qrSize}
              onChange={handleSizeChange}
              className="form-control"
            />
          </div>
        </div>

        {/* Download Button */}
        <button
          className="btn btn-success mt-3"
          onClick={handleDownload}
        >
          Download Customized QR
        </button>
      </div>
    </div>
  );
}

export default QREditor;
