import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');

  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Extract `templateName` from URL query parameters
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateNameParam = urlParams.get('templateName');
    if (templateNameParam) {
      setFileUrl(`http://localhost:8000/api/default/${templateNameParam}`);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const urlParams = new URLSearchParams(window.location.search);
    const templateName = urlParams.get('templateName');

    if (!templateName) {
      alert('Template name is missing in the URL!');
      return;
    }

    const contentArray = [];

    titles.forEach((title) => {
      contentArray.push({ type: 'text', data: title });
    });

    descriptions.forEach((description) => {
      contentArray.push({ type: 'text', data: description });
    });

    links.forEach((link) => {
      contentArray.push({ type: 'link', data: link });
    });

    images.forEach((image) => {
      if (image) {
        contentArray.push({ type: 'image', data: image });
      }
    });

    const formData = {
      title: brandName,
      slug: brandName.toLowerCase().replace(/ /g, '-'),
      template: templateName,
      contentArray: contentArray,
    };

    const submitUrl = `http://localhost:8000/api/default/${templateName}`;

    try {
      const response = await fetch(submitUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleChange = (index, field, value) => {
    switch (field) {
      case 'title':
        const newTitles = [...titles];
        newTitles[index] = value;
        setTitles(newTitles);
        break;
      case 'description':
        const newDescriptions = [...descriptions];
        newDescriptions[index] = value;
        setDescriptions(newDescriptions);
        break;
      case 'link':
        const newLinks = [...links];
        newLinks[index] = value;
        setLinks(newLinks);
        break;
      default:
        break;
    }
  };

  const handleImageChange = (index, file) => {
    const newImages = [...images];
    newImages[index] = file;
    setImages(newImages);
  };

  const handleRemove = (index, field) => {
    switch (field) {
      case 'title':
        setTitles(titles.filter((_, i) => i !== index));
        break;
      case 'description':
        setDescriptions(descriptions.filter((_, i) => i !== index));
        break;
      case 'link':
        setLinks(links.filter((_, i) => i !== index));
        break;
      case 'image':
        setImages(images.filter((_, i) => i !== index));
        break;
      default:
        break;
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Edit Template</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '20px' }}>
          {/* Left Side: Form */}
          <div style={{ width: 'calc(60% + 10px)', paddingRight: '5%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="d-flex justify-content-between mb-4">
                    <button type="button" className="btn btn-primary" onClick={handleAddTitle}>Add Title</button>
                    <button type="button" className="btn btn-primary" onClick={handleAddDescription}>Add Description</button>
                    <button type="button" className="btn btn-primary" onClick={handleAddLink}>Add Link</button>
                    <button type="button" className="btn btn-primary" onClick={handleAddImage}>Add Image</button>
                  </div>

                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dynamically Render Input Fields */}
                  {titles.map((title, index) => (
                    <div key={index} className="mb-3">
                      <label htmlFor={`title-${index}`} className="form-label">Title {index + 1}</label>
                      <input
                        type="text"
                        id={`title-${index}`}
                        className="form-control"
                        value={title}
                        onChange={(e) => handleChange(index, 'title', e.target.value)}
                      />
                      <button type="button" className="btn btn-danger mt-2" onClick={() => handleRemove(index, 'title')}>Remove Title</button>
                    </div>
                  ))}

                  <button type="submit" className="btn btn-success">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Mobile Preview */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px',
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                width: '100%',
                height: '667px',
                maxWidth: '375px',
                margin: 'auto',
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {fileUrl ? (
                <iframe
                  src={fileUrl}
                  width="100%"
                  height="100%"
                  style={{ border: 'none' }}
                  title="Mobile Template Preview"
                />
              ) : (
                <p style={{ textAlign: 'center', color: '#999' }}>No template selected. Upload or select a template to preview.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
