import React, { useState, useEffect } from 'react';

const Edit = () => {
  const [fileUrl, setFileUrl] = useState(''); // Keeping this for iframe preview if needed later
  const [htmlContent, setHtmlContent] = useState(''); // To store HTML response
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId');

    if (templateId) {
      const fetchHtmlContent = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-html/${templateId}`);
          if (response.ok) {
            const html = await response.text(); // Assuming the API returns raw HTML
            setHtmlContent(html);
          } else {
            console.error('Error fetching HTML content:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching HTML content:', error);
        }
      };

      fetchHtmlContent();
    }
  }, []);

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '10px' }}>
          {/* Left Side: Form */}
          <div style={{ width: 'calc(70%)', paddingRight: '5%', overflowY: 'auto', maxHeight: '80vh' }}>
          <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="d-flex justify-content-between mb-4">
                    <button type="button" className="btn btn-primary waves-effect waves-light" onClick={handleAddTitle}>Add Title</button>
                    <button type="button" className="btn btn-primary waves-effect waves-light" onClick={handleAddDescription}>Add Description</button>
                    <button type="button" className="btn btn-primary waves-effect waves-light" onClick={handleAddLink}>Add Link</button>
                    <button type="button" className="btn btn-primary waves-effect waves-light" onClick={handleAddImage}>Add Image</button>
                  </div>

                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                      style={{ padding: '10px' }} // Increase input padding for larger form
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                      style={{ padding: '10px' }} // Increase textarea padding for larger form
                    />
                  </div>

                  {/* Add Title Fields */}
                  {titles.map((title, index) => (
                    <div key={index} className="mb-3">
                      <label htmlFor={`title-${index}`} className="form-label">Title {index + 1}</label>
                      <input
                        type="text"
                        id={`title-${index}`}
                        className="form-control"
                        value={title}
                        onChange={(e) => handleChange(index, 'title', e.target.value)}
                        style={{ padding: '10px' }} // Increase input padding for larger form
                      />
                      <button
                        type="button"
                        className="btn btn-danger waves-effect waves-light mt-2"
                        onClick={() => handleRemove(index, 'title')}
                      >
                        Remove Title
                      </button>
                    </div>
                  ))}

                  {/* Add Description Fields */}
                  {descriptions.map((description, index) => (
                    <div key={index} className="mb-3">
                      <label htmlFor={`description-${index}`} className="form-label">Description {index + 1}</label>
                      <textarea
                        id={`description-${index}`}
                        className="form-control"
                        value={description}
                        onChange={(e) => handleChange(index, 'description', e.target.value)}
                        style={{ padding: '10px' }} // Increase textarea padding for larger form
                      />
                      <button
                        type="button"
                        className="btn btn-danger waves-effect waves-light mt-2"
                        onClick={() => handleRemove(index, 'description')}
                      >
                        Remove Description
                      </button>
                    </div>
                  ))}

                  {/* Add Link Fields */}
                  {links.map((link, index) => (
                    <div key={index} className="mb-3">
                      <label htmlFor={`link-${index}`} className="form-label">Link {index + 1}</label>
                      <input
                        type="url"
                        id={`link-${index}`}
                        className="form-control"
                        value={link}
                        onChange={(e) => handleChange(index, 'link', e.target.value)}
                        style={{ padding: '10px' }} // Increase input padding for larger form
                      />
                      <button
                        type="button"
                        className="btn btn-danger waves-effect waves-light mt-2"
                        onClick={() => handleRemove(index, 'link')}
                      >
                        Remove Link
                      </button>
                    </div>
                  ))}

                  {/* Add Image Fields */}
                  {images.map((image, index) => (
                    <div key={index} className="mb-3">
                      <label htmlFor={`image-${index}`} className="form-label">Image {index + 1}</label>
                      <input
                        type="file"
                        id={`image-${index}`}
                        className="form-control"
                        onChange={(e) => handleImageChange(index, e.target.files[0])}
                      />
                      <button
                        type="button"
                        className="btn btn-danger waves-effect waves-light mt-2"
                        onClick={() => handleRemove(index, 'image')}
                      >
                        Remove Image
                      </button>
                    </div>
                  ))}

                  <button type="submit" className="btn btn-success waves-effect waves-light">Submit</button>
                </form>
              </div>
            </div>          </div>

          {/* Right Side: Mobile Frame */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px',
              height: 'auto',
              maxHeight: '90vh',
              paddingRight: '0',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '100%', // Fill the container entirely
                height: '667px',
                maxWidth: '375px',
                margin: 'auto',
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {htmlContent ? (
                <div
                  dangerouslySetInnerHTML={{ __html: htmlContent }}
                  style={{
                    width: '100%',
                    height: '100%',
                    overflow: 'auto', // Add scrolling if content overflows
                  }}
                />
              ) : (
                <p style={{ textAlign: 'center', color: '#999' }}>
                  No content to preview. Ensure the template is selected or uploaded.
                </p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
