import React, { useState } from 'react';
import axios from 'axios';

const EditTemplate = () => {
  // State for the form inputs
  const [title, setTitle] = useState('');
  const [slug, setSlug] = useState('');
  const [template, setTemplate] = useState('flowpage1');
  const [contentArray, setContentArray] = useState([{ type: 'text', data: '' }]);

  // Handle the form changes
  const handleTitleChange = (e) => setTitle(e.target.value);
  const handleSlugChange = (e) => setSlug(e.target.value);
  const handleTemplateChange = (e) => setTemplate(e.target.value);

  const handleContentChange = (index, e) => {
    const newContentArray = [...contentArray];
    newContentArray[index][e.target.name] = e.target.value;
    setContentArray(newContentArray);
  };

  const handleImageUpload = (index, e) => {
    const newContentArray = [...contentArray];
    newContentArray[index].data = e.target.files[0]; // Store the file reference
    setContentArray(newContentArray);
  };

  const addContentBlock = () => {
    setContentArray([...contentArray, { type: 'text', data: '' }]);
  };

  const removeContentBlock = (index) => {
    const newContentArray = contentArray.filter((_, i) => i !== index);
    setContentArray(newContentArray);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('slug', slug);
    formData.append('template', template);
    contentArray.forEach((block, index) => {
      formData.append(`contentArray[${index}].type`, block.type);
      formData.append(`contentArray[${index}].data`, block.data); // For images, this will be the file object
    });

    try {
      const response = await axios.post('http://localhost:8000/api/create', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      console.log('Flowpage created successfully:', response.data);
    } catch (error) {
      console.error('Error creating Flowpage:', error);
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Edit Brand Information</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="row">
          {/* Left side: Form */}
          <div className="col-lg-6">
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Title */}
                  <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input
                      type="text"
                      id="title"
                      className="form-control"
                      value={title}
                      onChange={handleTitleChange}
                      required
                    />
                  </div>

                  {/* Slug */}
                  <div className="mb-3">
                    <label htmlFor="slug" className="form-label">Slug</label>
                    <input
                      type="text"
                      id="slug"
                      className="form-control"
                      value={slug}
                      onChange={handleSlugChange}
                      required
                    />
                  </div>

                  {/* Template Dropdown */}
                  <div className="mb-3">
                    <h4 className="header-title">Template</h4>
                    <div className="btn-group me-1 mt-1">
                      <button type="button" className="btn btn-primary dropdown-toggle" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        {template} <i className="mdi mdi-chevron-down"></i>
                      </button>
                      <div className="dropdown-menu">
                        <a className="dropdown-item" onClick={() => setTemplate('flowpage1')}>Flowpage 1</a>
                        <a className="dropdown-item" onClick={() => setTemplate('flowpage2')}>Flowpage 2</a>
                        <a className="dropdown-item" onClick={() => setTemplate('defaultTemplate')}>Default Template</a>
                      </div>
                    </div>
                  </div>

                  {/* Content Blocks */}
                  <h3 className="header-title">Content Blocks</h3>
                  {contentArray.map((block, index) => (
                    <div key={index} className="mb-3">
                      {/* Content Type Dropdown */}
                      <label className="form-label">Content Type</label>
                      <div className="btn-group me-1 mt-1">
                        <button type="button" className="btn btn-secondary dropdown-toggle" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                          {block.type.charAt(0).toUpperCase() + block.type.slice(1)} <i className="mdi mdi-chevron-down"></i>
                        </button>
                        <div className="dropdown-menu">
                          <a className="dropdown-item" onClick={() => handleContentChange(index, { target: { name: 'type', value: 'text' } })}>Text</a>
                          <a className="dropdown-item" onClick={() => handleContentChange(index, { target: { name: 'type', value: 'link' } })}>Link</a>
                          <a className="dropdown-item" onClick={() => handleContentChange(index, { target: { name: 'type', value: 'image' } })}>Image</a>
                        </div>
                      </div>

                      {/* Content Data */}
                      <div className="mt-2">
                        {block.type === 'image' ? (
                          <>
                            <label htmlFor={`contentData-${index}`} className="form-label">Upload Image</label>
                            <input
                              type="file"
                              name="data"
                              id={`contentData-${index}`}
                              onChange={(e) => handleImageUpload(index, e)}
                              className="form-control"
                            />
                            {block.data && (
                              <div className="mt-2">
                                <img
                                  src={URL.createObjectURL(block.data)}
                                  alt="Image Preview"
                                  style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                                />
                              </div>
                            )}
                          </>
                        ) : (
                          <input
                            type="text"
                            name="data"
                            id={`contentData-${index}`}
                            value={block.data}
                            onChange={(e) => handleContentChange(index, e)}
                            className="form-control"
                            required
                          />
                        )}
                      </div>

                      {/* Remove Block */}
                      <button
                        type="button"
                        className="btn btn-danger waves-effect waves-light mt-2"
                        onClick={() => removeContentBlock(index)}
                      >
                        Remove Block
                      </button>
                    </div>
                  ))}

                  {/* Add New Content Block */}
                  <button
                    type="button"
                    className="btn btn-primary waves-effect waves-light mt-3"
                    onClick={addContentBlock}
                  >
                    Add More Content
                  </button>

                  {/* Submit */}
                  <div className="mt-4">
                    <button type="submit" className="btn btn-success waves-effect waves-light">
                      Submit
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>

          {/* Right side: Mobile Frame Preview */}
          <div className="col-lg-6">
            <div className="card" style={{ position: 'relative', maxWidth: '300px', margin: '0 auto' }}>
              <div className="card-body">
                <div style={{ width: '100%', height: '600px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', backgroundColor: '#f5f5f5', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
                  {/* Mobile Frame */}
                  <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'flex-start', height: '100%' }}>
                    <h4 style={{ fontSize: '1.5em', marginBottom: '10px' }}>{title || 'Title of the Flowpage'}</h4>
                    <p style={{ fontSize: '1.2em', marginBottom: '10px' }}>{slug || 'Slug here'}</p>
                    {contentArray.map((block, index) => (
                      <div key={index} style={{ marginBottom: '20px' }}>
                        {block.type === 'text' && <p>{block.data}</p>}
                        {block.type === 'link' && <a href={block.data} target="_blank" rel="noopener noreferrer">{block.data}</a>}
                        {block.type === 'image' && block.data && <img src={URL.createObjectURL(block.data)} alt="Image Preview" style={{ width: '100%', maxHeight: '200px', objectFit: 'cover' }} />}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
