import React, { useState, useEffect } from 'react';

const Edit = () => {
  const [htmlContent, setHtmlContent] = useState(''); // For fetched HTML content
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  // Fetch HTML content when the component loads
  useEffect(() => {
    const fetchHtmlContent = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/get/sug`);
        if (response.ok) {
          const html = await response.text(); // Parse HTML content as text
          setHtmlContent(html); // Set the HTML content
        } else {
          console.error('Error fetching HTML content:', response.statusText);
          setHtmlContent('<p>Failed to load content.</p>'); // Fallback content
        }
      } catch (error) {
        console.error('Error fetching HTML content:', error);
        setHtmlContent('<p>Error loading content.</p>'); // Fallback content
      }
    };

    fetchHtmlContent();
  }, []);

  // Form Submission Handler
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      brandName,
      brandDescription,
      titles,
      descriptions,
      links,
      images,
    });
  };

  // Handlers for dynamic fields
  const handleAddField = (setter, defaultValue = '') => setter((prev) => [...prev, defaultValue]);

  const handleFieldChange = (index, setter, value) => setter((prev) => prev.map((item, i) => (i === index ? value : item)));

  const handleRemoveField = (index, setter) => setter((prev) => prev.filter((_, i) => i !== index));

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '10px' }}>
          {/* Form Section */}
          <div style={{ width: 'calc(70%)', paddingRight: '5%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Brand Description */}
                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dynamic Fields */}
                  {['Title', 'Description', 'Link', 'Image'].map((field) => (
                    <div key={field} className="mb-3">
                      <h5>{field}s</h5>
                      <button
                        type="button"
                        className="btn btn-primary"
                        onClick={() => handleAddField(eval(`set${field}s`), field === 'Image' ? null : '')}
                      >
                        Add {field}
                      </button>
                      {eval(`${field.toLowerCase()}s`).map((item, index) => (
                        <div key={index} className="mt-2">
                          <input
                            type={field === 'Image' ? 'file' : 'text'}
                            className="form-control"
                            value={field !== 'Image' ? item : undefined}
                            onChange={(e) =>
                              field === 'Image'
                                ? handleFieldChange(index, setImages, e.target.files[0])
                                : handleFieldChange(index, eval(`set${field}s`), e.target.value)
                            }
                          />
                          <button
                            type="button"
                            className="btn btn-danger mt-1"
                            onClick={() => handleRemoveField(index, eval(`set${field}s`))}
                          >
                            Remove
                          </button>
                        </div>
                      ))}
                    </div>
                  ))}

                  {/* Submit Button */}
                  <button type="submit" className="btn btn-success">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Preview Section */}
          <div style={{ width: '30%', position: 'sticky', top: '20px' }}>
            <div
              style={{
                width: '100%',
                height: '667px',
                maxWidth: '375px',
                margin: 'auto',
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {htmlContent ? (
                <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
              ) : (
                <p>No content to preview.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
