import { useState } from 'react';
import { QRCodeCanvas } from 'qrcode.react';

function QRCodeDesign() {
  const [qrData, setQrData] = useState('');
  const [selectedTemplate, setSelectedTemplate] = useState('template1');
  const [errorMessage, setErrorMessage] = useState('');

  // Template configurations
  const templates = {
    template1: {
      name: 'Business Card',
      bgImage: '/path-to-business-card-template.jpg',
      qrPosition: { top: '70%', left: '50%' }, // Position of QR code on the template
    },
    template2: {
      name: 'Flyer',
      bgImage: '/path-to-flyer-template.jpg',
      qrPosition: { top: '60%', left: '40%' },
    },
    template3: {
      name: 'Social Media',
      bgImage: '/path-to-social-media-template.jpg',
      qrPosition: { top: '50%', left: '50%' },
    },
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!qrData) {
      setErrorMessage('Please enter QR code data');
      return;
    }
    setErrorMessage('');
  };

  return (
    <div className="container">
      <h2>Generate QR Code with Design Templates</h2>

      {/* Error Message */}
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

      {/* Form */}
      <form onSubmit={handleSubmit}>
        {/* QR Data Input */}
        <label>Enter QR Code Data</label>
        <input
          type="text"
          className="form-control"
          value={qrData}
          onChange={(e) => setQrData(e.target.value)}
        />

        {/* Template Selection */}
        <label>Select Design Template</label>
        <select
          className="form-control"
          value={selectedTemplate}
          onChange={(e) => setSelectedTemplate(e.target.value)}
        >
          {Object.keys(templates).map((templateKey) => (
            <option key={templateKey} value={templateKey}>
              {templates[templateKey].name}
            </option>
          ))}
        </select>

        <button type="submit" className="btn btn-primary mt-3">
          Generate QR Code
        </button>
      </form>

      {/* QR Code Preview */}
      {qrData && (
        <div className="qr-preview mt-4" style={{ position: 'relative' }}>
          <h3>Preview</h3>

          {/* Template Background */}
          <div
            style={{
              position: 'relative',
              backgroundImage: `url(${templates[selectedTemplate].bgImage})`,
              width: '500px',
              height: '700px',
              backgroundSize: 'cover',
              border: '1px solid #ccc',
            }}
          >
            {/* QR Code Overlay */}
            <div
              style={{
                position: 'absolute',
                top: templates[selectedTemplate].qrPosition.top,
                left: templates[selectedTemplate].qrPosition.left,
                transform: 'translate(-50%, -50%)',
              }}
            >
              <QRCodeCanvas
                value={qrData}
                size={150}
                bgColor="white"
                fgColor="black"
                level="L"
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default QRCodeDesign;
