import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for making HTTP requests

function TemplateShowcase() {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]); // State to store the fetched files
  const [loading, setLoading] = useState(true); // Loading state for fetching files
  const [error, setError] = useState(null); // Error state for handling API errors

  // Fetch files from the backend
  const fetchFiles = async () => {
    try {
      const response = await axios.get('http://localhost:7000/api/get-files'); // Change to your actual API URL
      setFiles(response.data); // Update state with the fetched files
      setLoading(false); // Set loading to false after fetching
    } catch (err) {
      setError('Failed to fetch files');
      setLoading(false); // Set loading to false on error
      console.error(err);
    }
  };

  useEffect(() => {
    fetchFiles(); // Fetch files when the component mounts
  }, []);

  // Handlers for the buttons
  const handleEdit = (templateId) => {
    console.log(`Edit template with ID: ${templateId}`);
    // Redirect to edit template page with templateId as a query parameter or part of the URL
    navigate(`/landing-page-editor?templateId=${templateId}`);
  };

  return (
    <div className="page-content">
      {/* Page Header */}
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Template Showcase</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Page Content */}
      <div className="container-fluid">
        <div className="page-content-wrapper">
          <div className="row">
            {/* Template Cards */}
            {loading ? (
              <p>Loading files...</p>
            ) : error ? (
              <p>{error}</p>
            ) : (
              files.map((template, index) => (
                <div className="col-md-4" key={index}>
                  <div className="card">
                    <div className="card-body">
                      <div className="template-screenshot">
                        <img
                          src={template.screenshotUrl} // Assuming 'screenshotUrl' is in the file object
                          alt={`Template ${index + 1}`}
                          className="img-fluid rounded"
                        />
                      </div>
                      <h5 className="mt-3 text-center">{template.name}</h5>
                      <div className="d-flex justify-content-between mt-3">
                        <button
                          className="btn btn-primary"
                          onClick={() => handleEdit(template.id)} // Call handleEdit with the template ID
                        >
                          Edit Template
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default TemplateShowcase;
