import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');

  // Get templateId from the URL query string
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId'); // Get templateId from URL
    if (templateId) {
      const fetchFileUrl = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          const data = await response.json();
          if (data.fileUrl) {
            setFileUrl(data.fileUrl); // Set the file URL state
          }
        } catch (error) {
          console.error('Error fetching file URL:', error);
        }
      };
      fetchFileUrl();
    }
  }, []); // Empty dependency array to run this once on mount

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            {/* Add your form content here */}
            {/* Placeholder for form */}
          </div>

          {/* Right Side: Sticky Mobile Frame with HTML Content */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px', // Keep it 20px from the top
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '350px', // Adjusted width for the mobile frame
                height: '650px', // Adjusted height
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                overflow: 'hidden',
                boxShadow: '0 0 20px rgba(0, 0, 0, 0.1)',
                backgroundColor: '#fff',
                marginLeft: 'auto',
                marginRight: 'auto',
              }}
            >
              {/* Render HTML content from the fetched file URL */}
              {fileUrl && (
                <iframe
                  src={fileUrl} // Use the URL from API response
                  width="100%"
                  height="100%"
                  style={{
                    border: 'none',
                    borderRadius: '20px',
                    overflow: 'hidden',
                  }}
                  title="Mobile Frame Content"
                ></iframe>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
