import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');

  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  const [templateName, setTemplateName] = useState('');
  const [htmlContent, setHtmlContent] = useState('');
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Extract `templateName` from URL query parameters
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateNameParam = urlParams.get('templateName');
    if (templateNameParam) {
      setTemplateName(templateNameParam);
      fetchTemplatePreview(templateNameParam);
    }
  }, []);

  const fetchTemplatePreview = async (templateName) => {
    try {
      const response = await fetch(`http://localhost:8000/api/get/${templateName}`);
      if (response.ok) {
        const html = await response.text();
        setHtmlContent(html);
      } else {
        console.error('Failed to fetch template preview.');
      }
    } catch (error) {
      console.error('Error fetching template preview:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const contentArray = [];

    titles.forEach((title) => contentArray.push({ type: 'text', data: title }));
    descriptions.forEach((description) => contentArray.push({ type: 'text', data: description }));
    links.forEach((link) => contentArray.push({ type: 'link', data: link }));
    images.forEach((image) => image && contentArray.push({ type: 'image', data: image }));

    const formData = new FormData();
    formData.append('title', brandName);
    formData.append('slug', brandName.toLowerCase().replace(/ /g, '-'));
    formData.append('template', templateName);
    formData.append('contentArray', JSON.stringify(contentArray));
    if (logo) formData.append('logo', logo);

    try {
      const response = await fetch(`http://localhost:8000/api/create`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (data.success) {
        setShowSuccessMessage(true);
        fetchTemplatePreview(templateName);
      } else {
        alert('Error saving data. Please try again.');
      }
    } catch (error) {
      alert('Network error. Please try again.');
    }
  };

  const handleAddField = (type) => {
    if (type === 'title') setTitles([...titles, '']);
    else if (type === 'description') setDescriptions([...descriptions, '']);
    else if (type === 'link') setLinks([...links, '']);
    else if (type === 'image') setImages([...images, null]);
  };

  const handleFieldChange = (index, type, value) => {
    if (type === 'title') setTitles(titles.map((t, i) => (i === index ? value : t)));
    else if (type === 'description') setDescriptions(descriptions.map((d, i) => (i === index ? value : d)));
    else if (type === 'link') setLinks(links.map((l, i) => (i === index ? value : l)));
  };

  const handleImageChange = (index, file) => {
    setImages(images.map((img, i) => (i === index ? file : img)));
  };

  const handleRemoveField = (index, type) => {
    if (type === 'title') setTitles(titles.filter((_, i) => i !== index));
    else if (type === 'description') setDescriptions(descriptions.filter((_, i) => i !== index));
    else if (type === 'link') setLinks(links.filter((_, i) => i !== index));
    else if (type === 'image') setImages(images.filter((_, i) => i !== index));
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <h4>Edit Template: {templateName || 'No Template Selected'}</h4>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '20px' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dynamic Fields */}
                  {titles.map((title, index) => (
                    <div key={index} className="mb-3">
                      <label>Title {index + 1}</label>
                      <input
                        type="text"
                        className="form-control"
                        value={title}
                        onChange={(e) => handleFieldChange(index, 'title', e.target.value)}
                      />
                      <button type="button" onClick={() => handleRemoveField(index, 'title')}>Remove</button>
                    </div>
                  ))}

                  <button type="submit" className="btn btn-success">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Mobile Preview */}
          <div style={{ width: '35%', height: 'auto' }}>
            {htmlContent ? (
              <iframe
                srcDoc={htmlContent}
                width="100%"
                height="100%"
                style={{ border: 'none' }}
                title="Template Preview"
              />
            ) : (
              <p>No preview available.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
