import React, { useState } from 'react';
import { SketchPicker } from 'react-color';

function CustomLandingPage() {
    const [brandName, setBrandName] = useState('');
    const [brandDescription, setBrandDescription] = useState('');
    const [instagramLink, setInstagramLink] = useState('');
    const [facebookLink, setFacebookLink] = useState('');
    const [whatsappLink, setWhatsappLink] = useState('');
    const [backgroundColor, setBackgroundColor] = useState('#ffffff');

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        const response = await fetch('http://localhost:7000/api/register_brand', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ brandName, brandDescription, instagramLink, facebookLink, whatsappLink, backgroundColor }),
        });

        if (response.ok) {
          alert('Brand registered successfully!');
          // Clear form or redirect as needed
          setBrandName('');
          setBrandDescription('');
          setInstagramLink('');
          setFacebookLink('');
          setWhatsappLink('');
          setBackgroundColor('#ffffff');
        } else {
          alert('Failed to register the brand. Please try again.');
        }
      } catch (error) {
        console.error('Error:', error);
        alert('An error occurred. Please try again later.');
      }
    };

    return (
      <>
        <div className="page-content" style={{ backgroundColor: backgroundColor }}>
          {/* Start page title */}
          <div className="page-title-box">
            <div className="container-fluid">
              <div className="row align-items-center">
                <div className="col-sm-6">
                  <div className="page-title">
                    <h4>Add Brand Template</h4>
                  </div>
                </div>
              </div>
            </div>
          </div>
          {/* End page title */}

          <div className="container-fluid">
            <div className="page-content-wrapper">
              <div className="row">
                <div className="col-lg-12">
                  <div className="card">
                    <div className="card-body">
                      <h4 className="header-title">Brand Information</h4>
                      <p className="card-title-desc">Fill all information below</p>
                      <form onSubmit={handleSubmit}>
                        {/* Brand Name */}
                        <div className="mb-3">
                          <label className="form-label" htmlFor="brandName">Brand Name</label>
                          <input
                            id="brandName"
                            name="brandName"
                            type="text"
                            className="form-control"
                            placeholder="Enter brand name"
                            value={brandName}
                            onChange={(e) => setBrandName(e.target.value)}
                            required
                          />
                        </div>

                        {/* Brand Description */}
                        <div className="mb-3">
                          <label className="form-label" htmlFor="brandDescription">Brand Description</label>
                          <textarea
                            className="form-control"
                            id="brandDescription"
                            rows={5}
                            placeholder="Enter brand description"
                            value={brandDescription}
                            onChange={(e) => setBrandDescription(e.target.value)}
                            required
                          />
                        </div>

                        {/* Instagram Link */}
                        <div className="mb-3">
                          <label className="form-label" htmlFor="instagramLink">Instagram Link</label>
                          <input
                            id="instagramLink"
                            type="url"
                            className="form-control"
                            placeholder="Enter Instagram URL"
                            value={instagramLink}
                            onChange={(e) => setInstagramLink(e.target.value)}
                          />
                        </div>

                        {/* Facebook Link */}
                        <div className="mb-3">
                          <label className="form-label" htmlFor="facebookLink">Facebook Link</label>
                          <input
                            id="facebookLink"
                            type="url"
                            className="form-control"
                            placeholder="Enter Facebook URL"
                            value={facebookLink}
                            onChange={(e) => setFacebookLink(e.target.value)}
                          />
                        </div>

                        {/* WhatsApp Link */}
                        <div className="mb-3">
                          <label className="form-label" htmlFor="whatsappLink">WhatsApp Link</label>
                          <input
                            id="whatsappLink"
                            type="url"
                            className="form-control"
                            placeholder="Enter WhatsApp URL"
                            value={whatsappLink}
                            onChange={(e) => setWhatsappLink(e.target.value)}
                          />
                        </div>

                        {/* Background Color Picker */}
                        <div className="mb-3">
                          <label className="form-label" htmlFor="backgroundColor">Background Color</label>
                          <SketchPicker
                            color={backgroundColor}
                            onChangeComplete={(color) => setBackgroundColor(color.hex)}
                          />
                        </div>

                        {/* Submit Button */}
                        <button type="submit" className="btn btn-primary">Add Brand</button>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
              {/* End row */}
            </div>
          </div> {/* End container-fluid */}
        </div>
        {/* End Page-content */}

        <footer className="footer">
          <div className="container-fluid">
            <div className="row">
              <div className="col-sm-6">
                © Your Company.
              </div>
              <div className="col-sm-6">
                <div className="text-sm-end d-none d-sm-block">
                  Crafted with <i className="mdi mdi-heart text-danger" /> by Your Company
                </div>
              </div>
            </div>
          </div>
        </footer>
      </>
    );
  }


export default CustomLandingPage
