import React, { useState } from 'react';

function QRCodeDesign() {
  const [qrData, setQrData] = useState('');  // QR Code Data
  const [selectedTemplate, setSelectedTemplate] = useState('classic');  // Template selection
  const [qrCodeUrl, setQrCodeUrl] = useState('');  // For storing generated QR code URL
  const [errorMessage, setErrorMessage] = useState('');  // For error messages

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Request to backend for generating QR code with template
      const response = await fetch('http://localhost:7000/generate-qr', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ data: qrData, template: selectedTemplate }),
      });

      const result = await response.json();

      if (response.ok) {
        setQrCodeUrl(result.qrCodeUrl);  // Set the generated QR code URL
        setErrorMessage('');
      } else {
        setQrCodeUrl('');
        setErrorMessage('Failed to generate QR code. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('An error occurred. Please try again later.');
    }
  };

  return (
    <>
      <div className="page-content">
        {/* Start page title */}
        <div className="page-title-box">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col-sm-6">
                <div className="page-title">
                  <h4>Generate QR Code</h4>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* End page title */}

        <div className="container-fluid">
          <div className="page-content-wrapper">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-body">
                    <h4 className="header-title">QR Code Information</h4>
                    <p className="card-title-desc">Enter data to generate a QR code</p>

                    {/* Error Message */}
                    {errorMessage && (
                      <div className="alert alert-danger" role="alert">
                        {errorMessage}
                      </div>
                    )}

                    <form onSubmit={handleSubmit}>
                      {/* QR Code Data */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="qrData">QR Code Data</label>
                        <input
                          id="qrData"
                          name="qrData"
                          type="text"
                          className="form-control"
                          placeholder="Enter data for QR code"
                          value={qrData}
                          onChange={(e) => setQrData(e.target.value)}
                          required
                        />
                      </div>

                      {/* Template Selection */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="template">Select QR Code Design</label>
                        <select
                          id="template"
                          name="template"
                          className="form-control"
                          value={selectedTemplate}
                          onChange={(e) => setSelectedTemplate(e.target.value)}
                        >
                          <option value="classic">Classic</option>
                          <option value="blue">Blue</option>
                          <option value="green">Green</option>
                        </select>
                      </div>

                      {/* Submit Button */}
                      <button type="submit" className="btn btn-primary">Generate QR Code</button>
                    </form>
                  </div>
                </div>
              </div>
            </div>
            {/* End row */}

            {/* QR Code Preview */}
            {qrCodeUrl && (
              <div className="mt-4">
                <h4>Generated QR Code</h4>
                <img src={qrCodeUrl} alt="Generated QR Code" style={{ width: '200px', height: '200px' }} />
                <a href={qrCodeUrl} download="generated-qr-code.png">
                  <button className="btn btn-success mt-2">Download QR Code</button>
                </a>
              </div>
            )}
          </div>
        </div> {/* End container-fluid */}
      </div>
      {/* End Page-content */}

      <footer className="footer">
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-6">
              © Your Company.
            </div>
            <div className="col-sm-6">
              <div className="text-sm-end d-none d-sm-block">
                Crafted with <i className="mdi mdi-heart text-danger" /> by Your Company
              </div>
            </div>
          </div>
        </div>
      </footer>
    </>
  );
}

export default QRCodeDesign;
