import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [brandTitle, setBrandTitle] = useState('');
  const [slug, setSlug] = useState('');

  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  const [htmlContent, setHtmlContent] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateNameParam = urlParams.get('templateName');

    if (templateNameParam) {
      setFileUrl(`http://localhost:8000/api/default/${templateNameParam}`);

      const fetchHtmlContent = async () => {
        try {
          const response = await fetch(`http://localhost:8000/api/default/${templateNameParam}`);
          if (response.ok) {
            const html = await response.text();
            setHtmlContent(html);
          } else {
            console.error('Error fetching HTML content:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching HTML content:', error);
        }
      };

      fetchHtmlContent();
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!slug) {
      alert('Please enter a slug!');
      return;
    }

    const contentArray = [
      ...titles.map((t) => ({ type: 'text', data: t })),
      ...descriptions.map((d) => ({ type: 'text', data: d })),
      ...links.map((l) => ({ type: 'link', data: l })),
      ...images.map((img) => (img ? { type: 'image', data: img } : null)).filter(Boolean),
    ];

    const formData = {
      title: brandTitle,
      slug: slug.toLowerCase().replace(/ /g, '-'),
      template: new URLSearchParams(window.location.search).get('templateName'),
      contentArray,
    };

    setLoading(true);

    try {
      const response = await fetch('http://localhost:8000/api/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const updatedHtmlResponse = await fetch(`http://localhost:8000/api/get/${slug}`);
        if (updatedHtmlResponse.ok) {
          const updatedHtml = await updatedHtmlResponse.text();
          setHtmlContent(updatedHtml);
        } else {
          console.error('Error fetching updated HTML content:', updatedHtmlResponse.statusText);
        }
      } else {
        console.error('Error submitting form:', await response.text());
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = (type) => {
    switch (type) {
      case 'title':
        setTitles([...titles, '']);
        break;
      case 'description':
        setDescriptions([...descriptions, '']);
        break;
      case 'link':
        setLinks([...links, '']);
        break;
      case 'image':
        setImages([...images, '']);
        break;
      default:
        break;
    }
  };

  const handleChange = (index, field, value) => {
    switch (field) {
      case 'title':
        setTitles(titles.map((t, i) => (i === index ? value : t)));
        break;
      case 'description':
        setDescriptions(descriptions.map((d, i) => (i === index ? value : d)));
        break;
      case 'link':
        setLinks(links.map((l, i) => (i === index ? value : l)));
        break;
      case 'image':
        setImages(images.map((img, i) => (i === index ? value : img)));
        break;
      default:
        break;
    }
  };

  const handleRemove = (index, field) => {
    switch (field) {
      case 'title':
        setTitles(titles.filter((_, i) => i !== index));
        break;
      case 'description':
        setDescriptions(descriptions.filter((_, i) => i !== index));
        break;
      case 'link':
        setLinks(links.filter((_, i) => i !== index));
        break;
      case 'image':
        setImages(images.filter((_, i) => i !== index));
        break;
      default:
        break;
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <h4>Edit Template</h4>
      </div>

      <div className="container">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="brandTitle">Brand Title</label>
            <input
              type="text"
              id="brandTitle"
              value={brandTitle}
              onChange={(e) => setBrandTitle(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="slug">Slug</label>
            <input
              type="text"
              id="slug"
              value={slug}
              onChange={(e) => setSlug(e.target.value)}
              required
            />
          </div>

          {/* Titles */}
          {titles.map((title, index) => (
            <div key={index}>
              <label>Title {index + 1}</label>
              <input
                type="text"
                value={title}
                onChange={(e) => handleChange(index, 'title', e.target.value)}
              />
              <button type="button" onClick={() => handleRemove(index, 'title')}>
                Remove
              </button>
            </div>
          ))}

          {/* Descriptions */}
          {descriptions.map((description, index) => (
            <div key={index}>
              <label>Description {index + 1}</label>
              <textarea
                value={description}
                onChange={(e) => handleChange(index, 'description', e.target.value)}
              />
              <button type="button" onClick={() => handleRemove(index, 'description')}>
                Remove
              </button>
            </div>
          ))}

          {/* Links */}
          {links.map((link, index) => (
            <div key={index}>
              <label>Link {index + 1}</label>
              <input
                type="url"
                value={link}
                onChange={(e) => handleChange(index, 'link', e.target.value)}
              />
              <button type="button" onClick={() => handleRemove(index, 'link')}>
                Remove
              </button>
            </div>
          ))}

          {/* Images */}
          {images.map((image, index) => (
            <div key={index}>
              <label>Image {index + 1}</label>
              <input
                type="text"
                value={image}
                onChange={(e) => handleChange(index, 'image', e.target.value)}
              />
              <button type="button" onClick={() => handleRemove(index, 'image')}>
                Remove
              </button>
            </div>
          ))}

          <div className="btn-group">
            <button type="button" onClick={() => handleAdd('title')}>Add Title</button>
            <button type="button" onClick={() => handleAdd('description')}>Add Description</button>
            <button type="button" onClick={() => handleAdd('link')}>Add Link</button>
            <button type="button" onClick={() => handleAdd('image')}>Add Image</button>
          </div>

          <button type="submit" disabled={loading}>
            {loading ? 'Saving...' : 'Submit'}
          </button>
        </form>

        {/* Preview */}
        <div style={{ marginTop: '20px' }}>
          {loading ? (
            <p>Loading template...</p>
          ) : htmlContent ? (
            <iframe srcDoc={htmlContent} title="Template Preview" style={{ width: '100%', height: '500px' }} />
          ) : (
            <p>No template selected.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
