import React, { useState } from 'react';
import './styles.css';

const App = () => {
  const [pageComponents, setPageComponents] = useState([]);
  const [lastYPosition, setLastYPosition] = useState(20); // Initial offset for the first element

  // Function to handle adding components by clicking in the sidebar
  const addComponent = (item) => {
    const newComponent = {
      ...item,
      id: `${item.id}-${Date.now()}`,
      y: lastYPosition, // The Y position will be set to lastYPosition
    };

    setPageComponents((prev) => [...prev, newComponent]);

    // Update lastYPosition (move it down by a fixed height for next element)
    const elementHeight = 100; // Define the height of a component (you can adjust this)
    setLastYPosition((prev) => prev + elementHeight + 20); // +20 for margin between components
  };

  // Function to delete components
  const deleteComponent = (id) => {
    setPageComponents((prevComponents) => {
      const filteredComponents = prevComponents.filter((comp) => comp.id !== id);

      // Adjust the lastYPosition based on the remaining components
      if (filteredComponents.length > 0) {
        setLastYPosition(filteredComponents[filteredComponents.length - 1].y + 120); // Adjust the offset
      } else {
        setLastYPosition(20); // Reset to initial position if no components left
      }

      return filteredComponents;
    });
  };

  return (
    <div className="app">
      <Sidebar addComponent={addComponent} />
      <MainContent
        pageComponents={pageComponents}
        deleteComponent={deleteComponent}
      />
    </div>
  );
};

// Sidebar with clickable items
const Sidebar = ({ addComponent }) => {
  const components = [
    { id: 'text1', type: 'TEXT', content: 'This is a text block.' },
    { id: 'button', type: 'BUTTON', content: 'Click Me' },
    { id: 'link', type: 'LINK', content: 'Visit Levi\'s', url: 'https://www.levi.com' },
    { id: 'image', type: 'IMAGE', content: 'Image of Jeans', url: 'https://via.placeholder.com/150' },
    { id: 'video', type: 'VIDEO', content: 'Product Video', url: 'https://www.youtube.com/embed/dQw4w9WgXcQ' },
  ];

  return (
    <div className="sidebar">
      <h2>Sidebar</h2>
      {components.map((item) => (
        <div
          key={item.id}
          className="draggable-item"
          onClick={() => addComponent(item)}
        >
          {item.content}
        </div>
      ))}
    </div>
  );
};

// Main content area where items will be added
const MainContent = ({ pageComponents, deleteComponent }) => {
  return (
    <div className="main-content">
      <h2>Main Content</h2>
      {/* Mobile Frame */}
      <div className="mobile-frame">
        <div className="mobile-content">
          {pageComponents.map((component) => (
            <Component
              key={component.id}
              component={component}
              deleteComponent={deleteComponent}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

// Component to render added items
const Component = ({ component, deleteComponent }) => {
  return (
    <div className="component" style={{ top: component.y }}>
      {component.type === 'TEXT' && <p>{component.content}</p>}
      {component.type === 'BUTTON' && <button>{component.content}</button>}
      {component.type === 'LINK' && <a href={component.url} target="_blank" rel="noopener noreferrer">{component.content}</a>}
      {component.type === 'IMAGE' && <img src={component.url} alt={component.content} />}
      {component.type === 'VIDEO' && <iframe src={component.url} title={component.content} width="100%" height="200" />}
      <button className="delete-btn" onClick={() => deleteComponent(component.id)}>
        Delete
      </button>
    </div>
  );
};

export default App;
