import React, { useState } from 'react';
import { SketchPicker } from 'react-color';

function CustomLandingPage() {
  const [userInfo, setUserInfo] = useState({
    name: '',
    dp: null,
    facebook: '',
    instagram: '',
    whatsapp: '',
    bgColor: '#ffffff',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const handleImageUpload = (e) => {
    setUserInfo({ ...userInfo, dp: URL.createObjectURL(e.target.files[0]) });
  };

  const handleColorChange = (color) => {
    setUserInfo({ ...userInfo, bgColor: color.hex });
  };

  return (
    <div style={{ backgroundColor: userInfo.bgColor, padding: '20px' }}>
      <input type="text" name="name" placeholder="Name" onChange={handleInputChange} />
      <input type="url" name="facebook" placeholder="Facebook URL" onChange={handleInputChange} />
      <input type="url" name="instagram" placeholder="Instagram URL" onChange={handleInputChange} />
      <input type="url" name="whatsapp" placeholder="WhatsApp URL" onChange={handleInputChange} />
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      <SketchPicker color={userInfo.bgColor} onChangeComplete={handleColorChange} />
      <div>
        <h2>{userInfo.name}</h2>
        {userInfo.dp && <img src={userInfo.dp} alt="User DP" />}
        <p>
          <a href={userInfo.facebook}>Facebook</a> | 
          <a href={userInfo.instagram}>Instagram</a> | 
          <a href={userInfo.whatsapp}>WhatsApp</a>
        </p>
      </div>
    </div>
  );
}

export default CustomLandingPage
