import React, { useState } from 'react';
import '/App.css'
const App = () => {
    const [widgets, setWidgets] = useState([]);
    const [customizations, setCustomizations] = useState({});

    const addWidget = (widgetType) => {
        const id = Date.now();
        let widgetHTML = '';
        switch (widgetType) {
            case 'text-block':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Text Block</h3>
                        <p className="text-gray-700">This is a sample text block. You can edit this text to display your own content.</p>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'social-icons':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Social Media Icons</h3>
                        <div className="flex space-x-4">
                            <i className="fab fa-facebook text-blue-600 text-2xl"></i>
                            <i className="fab fa-twitter text-blue-400 text-2xl"></i>
                            <i className="fab fa-instagram text-pink-500 text-2xl"></i>
                        </div>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'link-button':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Link Button</h3>
                        <a className="block text-center bg-blue-500 text-white py-2 rounded-lg" href="#">Click Me</a>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'image-block':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Image Block</h3>
                        <img alt="Sample image block" className="w-full rounded-lg" height="200" src="https://placehold.co/300x200" width="300"/>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'video-block':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Video Block</h3>
                        <video className="w-full rounded-lg" controls>
                            <source src="https://www.w3schools.com/html/mov_bbb.mp4" type="video/mp4"/>
                            Your browser does not support the video tag.
                        </video>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'file-download':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">File Download Button</h3>
                        <a className="block text-center bg-green-500 text-white py-2 rounded-lg" href="#">Download File</a>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'contact-form':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Contact Form</h3>
                        <form>
                            <div className="mb-4">
                                <label className="block text-gray-700">Name</label>
                                <input className="w-full p-2 border rounded-lg" type="text"/>
                            </div>
                            <div className="mb-4">
                                <label className="block text-gray-700">Email</label>
                                <input className="w-full p-2 border rounded-lg" type="email"/>
                            </div>
                            <div className="mb-4">
                                <label className="block text-gray-700">Message</label>
                                <textarea className="w-full p-2 border rounded-lg"></textarea>
                            </div>
                            <button className="w-full bg-blue-500 text-white py-2 rounded-lg" type="submit">Send</button>
                        </form>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
            case 'payment-link':
                widgetHTML = (
                    <div className="bg-white p-4 rounded-lg shadow" key={id}>
                        <h3 className="text-lg font-semibold">Payment Link</h3>
                        <a className="block text-center bg-red-500 text-white py-2 rounded-lg" href="#">Donate Now</a>
                        <button className="text-red-500 mt-2" onClick={() => deleteWidget(id)}>Delete</button>
                    </div>
                );
                break;
        }
        setWidgets([...widgets, { id, widgetHTML }]);
        setCustomizations({ ...customizations, [id]: false });
    };

    const toggleCustomization = (id) => {
        setCustomizations({ ...customizations, [id]: !customizations[id] });
    };

    const deleteWidget = (id) => {
        setWidgets(widgets.filter(widget => widget.id !== id));
        const newCustomizations = { ...customizations };
        delete newCustomizations[id];
        setCustomizations(newCustomizations);
    };

    const savePage = () => {
        const pageContent = document.getElementById('mobile-preview').innerHTML;
        localStorage.setItem('savedPage', pageContent);
        alert('Page saved successfully!');
    };

    const publishPage = () => {
        const pageContent = document.getElementById('mobile-preview').innerHTML;
        // Here you would typically send the pageContent to your server to be published
        alert('Page published successfully!');
    };

    return (
        <div className="flex h-screen">
            <div className="w-1/2 bg-gray-50 p-6 overflow-y-scroll">
                <div className="space-y-6">
                    <div>
                        <h2 className="text-2xl font-bold mb-4">Widget Library</h2>
                        <div className="space-y-4">
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('text-block')}>
                                <i className="fas fa-font text-xl"></i>
                                <span>Text Block</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('social-icons')}>
                                <i className="fas fa-share-alt text-xl"></i>
                                <span>Social Media Icons</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('link-button')}>
                                <i className="fas fa-link text-xl"></i>
                                <span>Link Button</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('image-block')}>
                                <i className="fas fa-image text-xl"></i>
                                <span>Image Block</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('video-block')}>
                                <i className="fas fa-video text-xl"></i>
                                <span>Video Block</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('file-download')}>
                                <i className="fas fa-file-download text-xl"></i>
                                <span>File Download Button</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('contact-form')}>
                                <i className="fas fa-envelope text-xl"></i>
                                <span>Contact Form</span>
                            </div>
                            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => addWidget('payment-link')}>
                                <i className="fas fa-donate text-xl"></i>
                                <span>Payment Link</span>
                            </div>
                        </div>
                    </div>
                    <div className="space-y-4">
                        {widgets.map((widget, index) => (
                            <div key={widget.id}>
                                <div className="flex justify-between items-center">
                                    <span>Widget {index + 1}</span>
                                    <button onClick={() => toggleCustomization(widget.id)} className="text-blue-500">
                                        Customize
                                    </button>
                                </div>
                                <div className="mt-4">{widget.widgetHTML}</div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
            <div id="mobile-preview" className="w-1/2 bg-white p-6 overflow-y-scroll">
                <div className="space-y-6">
                    <h2 className="text-2xl font-bold mb-4">Preview</h2>
                    <div className="flex justify-between space-x-4">
                        <button onClick={savePage} className="w-1/2 bg-green-500 text-white py-2 rounded-lg">Save</button>
                        <button onClick={publishPage} className="w-1/2 bg-blue-500 text-white py-2 rounded-lg">Publish</button>
                    </div>
                    <div className="mt-6">
                        {widgets.length === 0 && <p className="text-gray-500 text-center">Add widgets to preview the page.</p>}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default App;
