import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [htmlContent, setHtmlContent] = useState(''); // State to store the HTML content
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [title1, setTitle1] = useState('');
  const [description1, setDescription1] = useState('');
  const [image1, setImage1] = useState(null);
  const [title2, setTitle2] = useState('');
  const [description2, setDescription2] = useState('');
  const [image2, setImage2] = useState(null);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Fetch the HTML file content
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId'); // Get templateId from URL

    if (templateId) {
      const fetchHtmlContent = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          if (!response.ok) throw new Error('Failed to load HTML file');
          const text = await response.text(); // Get HTML content as text
          setHtmlContent(text); // Set HTML content in state
        } catch (error) {
          console.error('Error fetching HTML content:', error);
        }
      };
      fetchHtmlContent();
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);
    formData.append('title1', title1);
    formData.append('description1', description1);
    formData.append('image1', image1);
    formData.append('title2', title2);
    formData.append('description2', description2);
    formData.append('image2', image2);

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Form fields here */}
                  {/* Example for Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>
                  {/* ... other form fields */}

                  {/* Success Message */}
                  {showSuccessMessage && (
                    <div className="alert alert-success mb-3" role="alert">
                      Data successfully added!
                    </div>
                  )}

                  <button type="submit" className="btn btn-primary">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Sticky Mobile Frame with HTML Content */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px',
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '350px',
                height: '650px',
                border: '16px solid #e7e7e7',
                borderRadius: '20px',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {/* Render HTML content directly inside the div */}
              {htmlContent && (
                <div
                  style={{
                    width: '100%',
                    height: '100%',
                    overflow: 'auto',
                  }}
                  dangerouslySetInnerHTML={{ __html: htmlContent }} // Render HTML content
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
