import React, { useState, useEffect } from 'react';
import { QRCodeCanvas } from 'qrcode.react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Use the useNavigate hook for redirection

function QRManagement() {
  const [qrList, setQrList] = useState([]);
  const navigate = useNavigate();  // Initialize the navigate function to redirect to a different page

  // Fetch QR codes from backend
  useEffect(() => {
    const fetchQRCodes = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/get_qrs');
        setQrList(response.data.Qrs);  // Access the Qrs array from response
      } catch (error) {
        console.error('Error fetching QR codes:', error);
      }
    };
    fetchQRCodes();
  }, []);

  // Handle "Book Slot" button click to navigate to the booking page with qrCodeId in the URL
  const handleBookSlot = (qrCodeId) => {
    // Redirect to the slot management page with the qrCodeId in the URL
    navigate(`/slot_management/${qrCodeId}`);
  };

  return (
    <div className="page-content">
      {/* start page title */}
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>QR Management</h4>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* end page title */}
      <div className="container-fluid">
        <div className="page-content-wrapper">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <div className="p-4" style={{ minHeight: 300 }}>
                    <h5>QR Code Management</h5>

                    {/* Render dynamic QR code list */}
                    <div className="row mt-4">
                      {qrList.map((qr) => (
                        <div key={qr.qrCodeId} className="col-md-4 text-center mb-3">
                          <QRCodeCanvas value={qr.url} size={100} />
                          <p className="mt-2">QR - {qr.qrCodeId}</p>

                          {/* Book Slot Button */}
                          <button
                            className="btn btn-primary"
                            onClick={() => handleBookSlot(qr.qrCodeId)}  // Pass qrCodeId in the URL
                          >
                            Book Slot
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </div> {/* end col */}
          </div> {/* end row */}
        </div>
      </div> {/* container-fluid */}
    </div>
  );
}

export default QRManagement;
