// QRManagement.js
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import QREditor from './QREditor'; // Import QREditor component
import { QRCodeCanvas } from 'qrcode.react';

function QRManagement() {
  const [qrList, setQrList] = useState([]);
  const [showEditor, setShowEditor] = useState({}); // Track which QR code is being edited
  const navigate = useNavigate();

  // Fetch QR codes from the backend
  useEffect(() => {
    const fetchQRCodes = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/get_qrs');
        setQrList(response.data.Qrs);
      } catch (error) {
        console.error('Error fetching QR codes:', error);
      }
    };
    fetchQRCodes();
  }, []);

  // Toggle the QR editor for a specific QR code
  const toggleEditor = (qrId) => {
    setShowEditor((prevState) => ({
      ...prevState,
      [qrId]: !prevState[qrId],
    }));
  };

  return (
    <div className="page-content">
      {/* start page title */}
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>QR Management</h4>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* end page title */}

      <div className="container-fluid">
        <div className="page-content-wrapper">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <div className="p-4" style={{ minHeight: 300 }}>
                    <h5>QR Code Management</h5>

                    {/* Render dynamic QR code list */}
                    <div className="row mt-4">
                      {qrList.map((qr, index) => {
                        const qrUrl = `https://qrbackend-aio3.onrender.com/api/redirect/${qr.qrCodeId}`;

                        return (
                          <div key={qr.qrCodeId} className="col-md-4 text-center mb-3">
                            {/* Display QR Code */}
                            <QRCodeCanvas
                              value={qrUrl}
                              size={150}
                              bgColor={"#ffffff"}
                              fgColor={"#000000"}
                              level={"H"}
                            />

                            {/* Display QR Code ID */}
                            <p className="mt-2">QR ID: {qr.qrCodeId}</p>

                            <div className="button-items mt-3">
                              {/* Book Slot Button */}
                              <button
                                className="btn btn-primary"
                                onClick={() => navigate('/slot_management')}
                              >
                                Book Slot
                              </button>

                              {/* Customize QR Button */}
                              <button
                                className="btn btn-secondary ml-2"
                                onClick={() => toggleEditor(qr.qrCodeId)}
                              >
                                Customize QR
                              </button>
                            </div>

                            {/* Show QREditor if toggled */}
                            {showEditor[qr.qrCodeId] && (
                              <QREditor qrCodeUrl={qrUrl} qrId={qr.qrCodeId} />
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            </div> {/* end col */}
          </div> {/* end row */}
        </div>
      </div> {/* container-fluid */}
    </div>
  );
}

export default QRManagement;
