// QREditor.js
import React, { useState } from 'react';
import { QRCodeCanvas } from 'qrcode.react';
import 'bootstrap/dist/css/bootstrap.min.css';

function QREditor({ qrCodeUrl, qrId }) {
  const [bgColor, setBgColor] = useState("#ffffff");
  const [fgColor, setFgColor] = useState("#000000");
  const [qrSize, setQrSize] = useState(150);

  // QR Code customization options
  const handleBgColorChange = (e) => setBgColor(e.target.value);
  const handleFgColorChange = (e) => setFgColor(e.target.value);
  const handleSizeChange = (e) => setQrSize(parseInt(e.target.value));

  // Download customized QR code
  const handleDownload = () => {
    const canvas = document.getElementById(`custom-qr-${qrId}`);
    const link = document.createElement("a");
    link.href = canvas.toDataURL("image/png");
    link.download = `Customized_QR_${qrId}.png`;
    link.click();
  };

  return (
    <div className="card mt-3">
      <div className="card-body">
        <h5 className="card-title">Edit QR Code for ID: {qrId}</h5>

        <div className="mb-3">
          {/* Display QR code with customization options */}
          <QRCodeCanvas
            id={`custom-qr-${qrId}`}
            value={qrCodeUrl}
            size={qrSize}
            bgColor={bgColor}
            fgColor={fgColor}
            level={"H"}
          />
        </div>

        {/* Customization Controls */}
        <div className="row">
          <div className="col-md-4">
            <label>Background Color:</label>
            <input
              type="color"
              value={bgColor}
              onChange={handleBgColorChange}
              className="form-control"
            />
          </div>
          <div className="col-md-4">
            <label>Foreground Color:</label>
            <input
              type="color"
              value={fgColor}
              onChange={handleFgColorChange}
              className="form-control"
            />
          </div>
          <div className="col-md-4">
            <label>QR Code Size:</label>
            <input
              type="number"
              min="100"
              max="300"
              value={qrSize}
              onChange={handleSizeChange}
              className="form-control"
            />
          </div>
        </div>

        {/* Download Button */}
        <button
          className="btn btn-success mt-3"
          onClick={handleDownload}
        >
          Download Customized QR
        </button>
      </div>
    </div>
  );
}

export default QREditor;
