import React, { useState, useEffect } from 'react';

const E = () => {
  const [qrId, setQrId] = useState('');
  const [date, setDate] = useState('');
  const [slotTime, setSlotTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [redirectUrl, setRedirectUrl] = useState('');
  const [brandNames, setBrandNames] = useState([]);
  const [qrIds, setQrIds] = useState([]);
  const [selectedBrandName, setSelectedBrandName] = useState('');
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // State for showing success message
  const [mobileData, setMobileData] = useState(null); // State for mobile frame data

  useEffect(() => {
    // Fetch QR IDs
    const fetchQrIds = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/get_qrs');
        const data = await response.json();
        if (data.success && Array.isArray(data.Qrs)) {
          setQrIds(data.Qrs);
        } else {
          console.error('Invalid response format for QR IDs:', data);
        }
      } catch (error) {
        console.error('Error fetching QR IDs:', error);
      }
    };

    fetchQrIds();
  }, []);

  useEffect(() => {
    // Fetch brand names
    const fetchBrandNames = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/get_brand');
        const data = await response.json();
        if (data.success && Array.isArray(data.Brands)) {
          setBrandNames(data.Brands);
        } else {
          console.error('Error: Brands data is not in expected array format.');
        }
      } catch (error) {
        console.error('Error fetching brand names:', error);
      }
    };

    fetchBrandNames();
  }, []);

  useEffect(() => {
    // Fetch mobile frame data
    const fetchMobileData = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/get_item');
        const data = await response.json();
        if (data.success) {
          setMobileData(data.item);
        } else {
          console.error('Error fetching mobile data:', data);
        }
      } catch (error) {
        console.error('Error fetching mobile data:', error);
      }
    };

    fetchMobileData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      qrId,
      brandId: selectedBrandName,
      date,
      startTime: slotTime,
      endTime,
      redirectionUrl: redirectUrl,
    };

    try {
      const response = await fetch('http://localhost:7000/api/add_slot', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true); // Show success message after slot is successfully added
      } else {
        alert('Error saving slot. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        {/* Left Side: Form */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <h4 className="mb-4">QR Slot Booking Form</h4>

                {/* QR ID Dropdown */}
                <div className="mb-3">
                  <label htmlFor="qrId" className="form-label">Select QR ID</label>
                  <select
                    id="qrId"
                    className="form-select"
                    value={qrId}
                    onChange={(e) => setQrId(e.target.value)}
                    required
                  >
                    <option value="">Select QR ID</option>
                    {qrIds.map((qr) => (
                      <option key={qr._id} value={qr.qrCodeId}>{qr.qrCodeId}</option>
                    ))}
                  </select>
                </div>

                {/* Brand Name Dropdown */}
                <div className="mb-3">
                  <label htmlFor="brandName" className="form-label">Select Brand Name</label>
                  <select
                    id="brandName"
                    className="form-select"
                    value={selectedBrandName}
                    onChange={(e) => setSelectedBrandName(e.target.value)}
                    required
                  >
                    <option value="">Select Brand Name</option>
                    {brandNames.map((brand) => (
                      <option key={brand._id} value={brand._id}>
                        {brand.brandName}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Date Input */}
                <div className="mb-3">
                  <label htmlFor="date" className="form-label">Select Date</label>
                  <input
                    type="date"
                    id="date"
                    className="form-control"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                  />
                </div>

                {/* Slot Time Input */}
                <div className="mb-3">
                  <label htmlFor="slotTime" className="form-label">Select Slot Time</label>
                  <input
                    type="time"
                    id="slotTime"
                    className="form-control"
                    value={slotTime}
                    onChange={(e) => setSlotTime(e.target.value)}
                    required
                  />
                </div>

                {/* End Time Input */}
                <div className="mb-3">
                  <label htmlFor="endTime" className="form-label">Select End Time</label>
                  <input
                    type="time"
                    id="endTime"
                    className="form-control"
                    value={endTime}
                    onChange={(e) => setEndTime(e.target.value)}
                    required
                  />
                </div>

                {/* Redirect URL Input */}
                <div className="mb-3">
                  <label htmlFor="redirectUrl" className="form-label">Redirect URL</label>
                  <input
                    type="url"
                    id="redirectUrl"
                    className="form-control"
                    value={redirectUrl}
                    onChange={(e) => setRedirectUrl(e.target.value)}
                    required
                  />
                </div>

                {/* Success Message */}
                {showSuccessMessage && (
                  <div className="alert alert-success mb-3" role="alert">
                    Slot successfully added!
                  </div>
                )}

                <button type="submit" className="btn btn-primary">Submit</button>
              </form>
            </div>
          </div>
        </div>

        {/* Right Side: Mobile Frame */}
        <div className="col-md-6 d-flex justify-content-center align-items-center">
          <div className="mobile-frame border p-4">
            <h5>Mobile Frame</h5>
            {mobileData ? (
              <div>
                <p><strong>ID:</strong> {mobileData.id}</p>
                <p><strong>Name:</strong> {mobileData.name}</p>
                <p><strong>Description:</strong> {mobileData.description}</p>
              </div>
            ) : (
              <p>Loading data...</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default E;
