import React, { useState } from 'react';
import { DndProvider, useDrag } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import './styles.css'; // Include your CSS here

const App = () => {
  // State for Hero section
  const [heroContent, setHeroContent] = useState({
    title: 'Summer Collection 2024',
    description: 'Explore the latest fashion trends.',
    ctaText: 'Shop Now',
    ctaLink: '#',
  });

  // State for products
  const [products, setProducts] = useState([
    {
      id: 1,
      title: '501® Original Fit Jeans',
      price: '$89.50',
      image: 'https://via.placeholder.com/200',
    },
    {
      id: 2,
      title: 'Trucker Jacket',
      price: '$69.50',
      image: 'https://via.placeholder.com/200',
    },
  ]);

  const [editable, setEditable] = useState(false);

  // Update hero content
  const handleUpdateHeroContent = (newContent) => {
    setHeroContent(newContent);
  };

  // EditableText component inside the same file
  const EditableText = ({ text, editable, onSave }) => {
    const [inputValue, setInputValue] = useState(text);

    const handleChange = (e) => {
      setInputValue(e.target.value);
    };

    return editable ? (
      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        onBlur={() => onSave(inputValue)}
      />
    ) : (
      <span>{text}</span>
    );
  };

  // DraggableProductCard component inside the same file
  const DraggableProductCard = ({ product }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
      type: 'PRODUCT',
      item: { id: product.id, type: 'PRODUCT' },
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }));

    return (
      <div
        ref={drag}
        className="product-card"
        style={{ opacity: isDragging ? 0.5 : 1 }}
      >
        <img src={product.image} alt={product.title} />
        <h3>{product.title}</h3>
        <p>{product.price}</p>
      </div>
    );
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="container">
        {/* Editable Hero Section */}
        <section className="hero">
          <h1>
            <EditableText
              text={heroContent.title}
              editable={editable}
              onSave={(newTitle) => handleUpdateHeroContent({ ...heroContent, title: newTitle })}
            />
          </h1>
          <p>
            <EditableText
              text={heroContent.description}
              editable={editable}
              onSave={(newDescription) =>
                handleUpdateHeroContent({ ...heroContent, description: newDescription })
              }
            />
          </p>
          <button className="cta-button">
            <EditableText
              text={heroContent.ctaText}
              editable={editable}
              onSave={(newCtaText) =>
                handleUpdateHeroContent({ ...heroContent, ctaText: newCtaText })
              }
            />
          </button>
          <button onClick={() => setEditable(!editable)}>
            {editable ? 'Done' : 'Edit'}
          </button>
        </section>

        {/* Editable Product List */}
        <div className="products-container">
          {products.map((product) => (
            <DraggableProductCard key={product.id} product={product} />
          ))}
        </div>
      </div>
    </DndProvider>
  );
};

export default App;
