import React, { useState, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');

  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId');
    if (templateId) {
      const fetchFileUrl = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          const data = await response.json();
          if (data.fileUrl) {
            setFileUrl(data.fileUrl);
          }
        } catch (error) {
          console.error('Error fetching file URL:', error);
        }
      };
      fetchFileUrl();
    }
  }, []);

  // const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);

    titles.forEach((title, index) => formData.append(`titles[${index}]`, title));
    descriptions.forEach((description, index) => formData.append(`descriptions[${index}]`, description));
    links.forEach((link, index) => formData.append(`links[${index}]`, link));
    images.forEach((image, index) => {
      if (image) formData.append(`images[${index}]`, image);
    });

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  const DraggableItem = ({ index, moveItem, type, children }) => {
    const [, drag] = useDrag({
      type: type,
      item: { index },
    });

    const [, drop] = useDrop({
      accept: type,
      hover: (item) => {
        if (item.index !== index) {
          moveItem(item.index, index, type);
          item.index = index;
        }
      },
    });

    return (
      <div ref={(node) => drag(drop(node))} style={{ padding: '8px', border: '1px solid #ddd', marginBottom: '8px' }}>
        {children}
      </div>
    );
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="page-content">
        <div className="page-title-box">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col-sm-6">
                <div className="page-title">
                  <h4>Brand Information Form</h4>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="container-fluid">
          <div className="page-content-wrapper" style={{ display: 'flex' }}>
            <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
              <div className="card">
                <div className="card-body">

                  {/* Add Buttons at the Top */}
                  <div className="add-buttons mb-4">
                    <button
                      type="button"
                      className="btn btn-primary me-2"
                      onClick={handleAddTitle}
                    >
                      Add Title
                    </button>
                    <button
                      type="button"
                      className="btn btn-primary me-2"
                      onClick={handleAddDescription}
                    >
                      Add Description
                    </button>
                    <button
                      type="button"
                      className="btn btn-primary me-2"
                      onClick={handleAddLink}
                    >
                      Add Link
                    </button>
                    <button
                      type="button"
                      className="btn btn-primary"
                      onClick={handleAddImage}
                    >
                      Add Image
                    </button>
                  </div>

                  {/* Form Content */}
                  <form onSubmit={handleSubmit}>
                    {/* Upload Logo */}
                    <div className="mb-3">
                      <label htmlFor="logo" className="form-label">Upload Logo</label>
                      <input
                        type="file"
                        id="logo"
                        className="form-control"
                        onChange={(e) => setLogo(e.target.files[0])}
                        required
                      />
                    </div>

                    {/* Brand Name */}
                    <div className="mb-3">
                      <label htmlFor="brandName" className="form-label">Brand Name</label>
                      <input
                        type="text"
                        id="brandName"
                        className="form-control"
                        value={brandName}
                        onChange={(e) => setBrandName(e.target.value)}
                        required
                      />
                    </div>

                    {/* Brand Description */}
                    <div className="mb-3">
                      <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                      <textarea
                        id="brandDescription"
                        className="form-control"
                        rows="3"
                        value={brandDescription}
                        onChange={(e) => setBrandDescription(e.target.value)}
                        required
                      />
                    </div>

                    {/* Dynamic Sections */}
                    {titles.map((title, index) => (
                      <DraggableItem key={index} index={index} type="title">
                        <div className="mb-3">
                          <label htmlFor={`title-${index}`} className="form-label">Title {index + 1}</label>
                          <input
                            type="text"
                            id={`title-${index}`}
                            className="form-control"
                            value={title}
                            onChange={(e) => {
                              const newTitles = [...titles];
                              newTitles[index] = e.target.value;
                              setTitles(newTitles);
                            }}
                          />
                        </div>
                      </DraggableItem>
                    ))}

                    {/* Other dynamic sections for descriptions, links, images */}
                    {/* Submit Button */}
                    <button type="submit" className="btn btn-success">
                      Submit
                    </button>
                  </form>
                </div>
              </div>
            </div>

            {/* Right Side */}
            <div style={{ width: '30%', position: 'sticky', top: '20px', height: 'auto', maxHeight: '90vh' }}>
              <div style={{
                position: 'sticky',
                top: '20px',
                width: '350px',
                height: '650px',
                border: '16px solid #e7e7e7',
                borderRadius: '20px',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}>
                {fileUrl && (
                  <iframe
                    src={fileUrl}
                    width="100%"
                    height="100%"
                    style={{ border: 'none', borderRadius: '20px' }}
                    title="Mobile Template"
                  />
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </DndProvider>
  );
};

export default EditTemplate;
