import React, { useEffect, useState } from 'react';
import axios from 'axios'; // import axios for API requests

const TemplateShowcase = () => {
  // State to hold the fetched templates
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch templates from the backend on component mount
  useEffect(() => {
    const fetchTemplates = async () => {
      try {
        const response = await axios.get('https://your-api-endpoint.com/templates'); // Replace with your API endpoint
        setTemplates(response.data); // Assuming the API returns an array of templates
        setLoading(false); // Set loading to false when data is fetched
      } catch (err) {
        setError('Error fetching templates');
        setLoading(false); // Set loading to false on error
      }
    };

    fetchTemplates();
  }, []); // Empty dependency array ensures the API call runs only once after the component mounts

  // If loading, show a loading message
  if (loading) {
    return <div className="text-center">Loading templates...</div>;
  }

  // If there's an error, show an error message
  if (error) {
    return <div className="text-center text-red-500">{error}</div>;
  }

  return (
    <div className="page-content">
        <div className="page-title-box">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col-sm-6">
                <div className="page-title">
                  <h4>Generate QR Code</h4>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="container-fluid">
          <div className="page-content-wrapper">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-body">
                    <h4 className="header-title">QR Code Information</h4>

    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <h2 className="text-3xl font-bold text-center mb-12">Our Templates</h2>

      {/* Grid container for the templates */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {/* Map through the templates array and create template cards */}
        {templates.map((template) => (
          <div key={template.id} className="bg-white p-4 rounded-lg shadow-lg flex justify-center items-center">
            <div className="w-64 h-128 bg-gray-200 rounded-lg p-4 flex flex-col items-center justify-between">
              <div className="bg-black w-full h-full rounded-lg relative">
                <img
                  src={template.image} // Dynamic image URL from the backend
                  alt={template.name}
                  className="w-full h-full object-cover rounded-lg"
                />
                <div className="absolute top-0 left-0 w-full h-full border-8 border-black rounded-lg">
                  <div className="absolute top-2 left-0 right-0 p-4">
                    <h3 className="text-xl font-bold text-white">{template.name}</h3>
                    <p className="text-white mt-2">{template.description}</p>
                    <button className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                      View Details
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
    </div>
                </div>
              </div>
            </div>
          </div>
    </div>
    </div>

  );
};

export default TemplateShowcase;
