import React, { useState, useEffect } from 'react';

const Edit = () => {
  const [htmlContent, setHtmlContent] = useState('');
  const [templateName, setTemplateName] = useState('');
  const [slug, setSlug] = useState('');
  const [title, setTitle] = useState('');
  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  // Fetch HTML content
  useEffect(() => {
    const fetchHtmlContent = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/get/sug`);
        if (response.ok) {
          const html = await response.text();
          setHtmlContent(html);
        } else {
          console.error('Error fetching HTML content:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching HTML content:', error);
      }
    };

    fetchHtmlContent();
  }, []);

  // Form Handlers
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the form data in the correct format
    const contentArray = [];

    // Add title to contentArray as type: text
    if (title) contentArray.push({ type: 'text', data: title });

    // Add description fields as type: text
    descriptions.forEach((d) => {
      if (d) contentArray.push({ type: 'text', data: d });
    });

    // Add links as type: link
    links.forEach((l) => {
      if (l) contentArray.push({ type: 'link', data: l });
    });

    // Add images as type: image
    images.forEach((img) => {
      if (img) contentArray.push({ type: 'image', data: img });
    });

    // Prepare the final data object
    const dataToSend = {
      title: title,
      slug: slug,
      template: templateName,
      contentArray: contentArray,
    };

    console.log('Data to send:', dataToSend);

    try {
      const response = await fetch('http://localhost:8000/api/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataToSend), // Send the formatted data
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Form submitted successfully:', data);
      } else {
        console.error('Error submitting form:', response.statusText);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };



  const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleChange = (index, type, value) => {
    switch (type) {
      case 'title':
        setTitles(titles.map((t, i) => (i === index ? value : t)));
        break;
      case 'description':
        setDescriptions(descriptions.map((d, i) => (i === index ? value : d)));
        break;
      case 'link':
        setLinks(links.map((l, i) => (i === index ? value : l)));
        break;
      default:
        break;
    }
  };

  const handleImageChange = (index, file) => {
    setImages(images.map((img, i) => (i === index ? file : img)));
  };

  const handleRemove = (index, type) => {
    switch (type) {
      case 'title':
        setTitles(titles.filter((_, i) => i !== index));
        break;
      case 'description':
        setDescriptions(descriptions.filter((_, i) => i !== index));
        break;
      case 'link':
        setLinks(links.filter((_, i) => i !== index));
        break;
      case 'image':
        setImages(images.filter((_, i) => i !== index));
        break;
      default:
        break;
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Template Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '10px' }}>
          {/* Left Side: Form */}
          <div style={{ width: 'calc(70%)', paddingRight: '5%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Dropdown Button for Adding Elements */}
                  <div className="btn-group me-1 mt-1">
                    <button
                      type="button"
                      className="btn btn-success dropdown-toggle"
                      data-bs-toggle="dropdown"
                      aria-haspopup="true"
                      aria-expanded="false"
                    >
                      Add Element <i className="mdi mdi-chevron-down"></i>
                    </button>
                    <div className="dropdown-menu">
                      <a className="dropdown-item" href="#" onClick={handleAddTitle}>
                        Add Title
                      </a>
                      <a className="dropdown-item" href="#" onClick={handleAddDescription}>
                        Add Description
                      </a>
                      <a className="dropdown-item" href="#" onClick={handleAddLink}>
                        Add Link
                      </a>
                      <a className="dropdown-item" href="#" onClick={handleAddImage}>
                        Add Image
                      </a>
                    </div>
                  </div>

                  {/* Title (Mandatory) */}
                  <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input
                      type="text"
                      id="title"
                      className="form-control"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      required
                    />
                  </div>

                  {/* Template Name (Mandatory) */}
                  <div className="mb-3">
                    <label htmlFor="templateName" className="form-label">Template Name</label>
                    <input
                      type="text"
                      id="templateName"
                      className="form-control"
                      value={templateName}
                      onChange={(e) => setTemplateName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Slug (Mandatory) */}
                  <div className="mb-3">
                    <label htmlFor="slug" className="form-label">Slug</label>
                    <input
                      type="text"
                      id="slug"
                      className="form-control"
                      value={slug}
                      onChange={(e) => setSlug(e.target.value)}
                      required
                    />
                  </div>

                  {titles.map((title, index) => (
                    <div key={index} className="mb-3">
                      <label className="form-label">Additional Title {index + 1}</label>
                      <input
                        type="text"
                        className="form-control"
                        value={title}
                        onChange={(e) => handleChange(index, 'title', e.target.value)}
                      />
                      <button
                        type="button"
                        className="btn btn-danger mt-2"
                        onClick={() => handleRemove(index, 'title')}
                      >
                        Remove
                      </button>
                    </div>
                  ))}

                  {descriptions.map((description, index) => (
                    <div key={index} className="mb-3">
                      <label className="form-label">Description {index + 1}</label>
                      <textarea
                        className="form-control"
                        rows="2"
                        value={description}
                        onChange={(e) => handleChange(index, 'description', e.target.value)}
                      />
                      <button
                        type="button"
                        className="btn btn-danger mt-2"
                        onClick={() => handleRemove(index, 'description')}
                      >
                        Remove
                      </button>
                    </div>
                  ))}

                  {links.map((link, index) => (
                    <div key={index} className="mb-3">
                      <label className="form-label">Link {index + 1}</label>
                      <input
                        type="url"
                        className="form-control"
                        value={link}
                        onChange={(e) => handleChange(index, 'link', e.target.value)}
                      />
                      <button
                        type="button"
                        className="btn btn-danger mt-2"
                        onClick={() => handleRemove(index, 'link')}
                      >
                        Remove
                      </button>
                    </div>
                  ))}

                  {images.map((image, index) => (
                    <div key={index} className="mb-3">
                      <label className="form-label">Image {index + 1}</label>
                      <input
                        type="file"
                        className="form-control"
                        onChange={(e) => handleImageChange(index, e.target.files[0])}
                      />
                      <button
                        type="button"
                        className="btn btn-danger mt-2"
                        onClick={() => handleRemove(index, 'image')}
                      >
                        Remove
                      </button>
                    </div>
                  ))}

                  <button type="submit" className="btn btn-success">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Mobile Frame */}
          <div style={{ width: '30%', position: 'sticky', top: '20px' }}>
            <div
              style={{
                width: '100%',
                height: '667px',
                maxWidth: '375px',
                margin: 'auto',
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {htmlContent ? (
                <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
              ) : (
                <p>No content to preview.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
