// QREditor.js
import React, { useState, useEffect } from 'react';
import { QRCodeCanvas } from 'qrcode.react';
import 'bootstrap/dist/css/bootstrap.min.css';

function QREditor({ qrCodeUrl, qrId }) {
  const [bgColor, setBgColor] = useState("#ffffff");
  const [fgColor, setFgColor] = useState("#000000");
  const [qrSize, setQrSize] = useState(150);

  useEffect(() => {
    // Ensure the QR code gets re-rendered whenever any customization changes
  }, [bgColor, fgColor, qrSize]);

  // Handle background color change
  const handleBgColorChange = (e) => setBgColor(e.target.value);

  // Handle foreground color change
  const handleFgColorChange = (e) => setFgColor(e.target.value);

  // Handle QR code size change
  const handleSizeChange = (e) => setQrSize(parseInt(e.target.value));

  // Handle download functionality
  const handleDownload = () => {
    const canvas = document.getElementById(`custom-qr-${qrId}`);
    const link = document.createElement("a");
    link.href = canvas.toDataURL("image/png");
    link.download = `Customized_QR_${qrId}.png`;
    link.click();
  };

  return (
    <div className="page-content">
    {/* start page title */}
    <div className="page-title-box">
      <div className="container-fluid">
    <div className="card mt-3">
      <div className="card-body">
        <h5 className="card-title">Edit QR Code for ID: {qrId}</h5>

        {/* QR Code Display with Customization */}
        <div className="mb-3">
          <QRCodeCanvas
            id={`custom-qr-${qrId}`}
            value={qrCodeUrl} // The URL will be passed as a prop
            size={qrSize} // QR size will be dynamically set
            bgColor={bgColor} // Background color based on state
            fgColor={fgColor} // Foreground color based on state
            level={"H"} // Error correction level
          />
        </div>

        {/* Customization Controls */}
        <div className="row">
          <div className="col-md-4">
            <label>Background Color:</label>
            <input
              type="color"
              value={bgColor}
              onChange={handleBgColorChange}
              className="form-control"
            />
          </div>
          <div className="col-md-4">
            <label>Foreground Color:</label>
            <input
              type="color"
              value={fgColor}
              onChange={handleFgColorChange}
              className="form-control"
            />
          </div>
          <div className="col-md-4">
            <label>QR Code Size:</label>
            <input
              type="number"
              min="100"
              max="300"
              value={qrSize}
              onChange={handleSizeChange}
              className="form-control"
            />
          </div>
        </div>

        {/* Download Button */}
        <button
          className="btn btn-success mt-3"
          onClick={handleDownload}
        >
          Download Customized QR
        </button>
      </div>
    </div>

    </div>

    </div>

    </div>
  );
}

export default QREditor;
