import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Edit = () => {
  const { templateName } = useParams();  // Capture the template name from the URL
  const [htmlContent, setHtmlContent] = useState('');
  const [slug, setSlug] = useState('');
  const [title, setTitle] = useState('');
  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  // Fetch HTML content based on template name from URL
  useEffect(() => {
    const fetchHtmlContent = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/get/${templateName}`);
        if (response.ok) {
          const html = await response.text();
          setHtmlContent(html);
        } else {
          console.error('Error fetching HTML content:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching HTML content:', error);
      }
    };

    fetchHtmlContent();
  }, [templateName]); // Re-fetch if templateName changes

  // Form Handlers
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the contentArray based on the form inputs
    const contentArray = [];

    // Add the primary text content (for titles, descriptions, etc.)
    titles.forEach((t) => contentArray.push({ type: 'text', data: t }));
    descriptions.forEach((d) => contentArray.push({ type: 'text', data: d }));
    links.forEach((l) => contentArray.push({ type: 'link', data: l }));
    images.forEach((img) => {
      if (img) {
        contentArray.push({ type: 'image', data: img });
      }
    });

    // Prepare the form data to match the API structure
    const formData = {
      title,
      slug,
      template: templateName,  // Using the templateName from the URL
      contentArray,
    };

    // Sending data to the backend
    try {
      const response = await fetch('http://localhost:8000/api/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',  // Send as JSON
        },
        body: JSON.stringify(formData),  // Send the structured formData
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Form submitted successfully:', data);

        // After successful submission, update the HTML content in the mobile frame using the new slug
        const updatedHtmlResponse = await fetch(`http://localhost:8000/api/get/${slug}`);
        if (updatedHtmlResponse.ok) {
          const updatedHtml = await updatedHtmlResponse.text();
          setHtmlContent(updatedHtml);
        } else {
          console.error('Error fetching updated HTML content:', updatedHtmlResponse.statusText);
        }
      } else {
        console.error('Error submitting form:', response.statusText);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleChange = (index, type, value) => {
    switch (type) {
      case 'title':
        setTitles(titles.map((t, i) => (i === index ? value : t)));
        break;
      case 'description':
        setDescriptions(descriptions.map((d, i) => (i === index ? value : d)));
        break;
      case 'link':
        setLinks(links.map((l, i) => (i === index ? value : l)));
        break;
      default:
        break;
    }
  };

  const handleImageChange = (index, file) => {
    setImages(images.map((img, i) => (i === index ? file : img)));
  };

  const handleRemove = (index, type) => {
    switch (type) {
      case 'title':
        setTitles(titles.filter((_, i) => i !== index));
        break;
      case 'description':
        setDescriptions(descriptions.filter((_, i) => i !== index));
        break;
      case 'link':
        setLinks(links.filter((_, i) => i !== index));
        break;
      case 'image':
        setImages(images.filter((_, i) => i !== index));
        break;
      default:
        break;
    }
  };

  return (
    <div className="page-content">
      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '10px' }}>
          {/* Left Side: Form */}
          <div style={{ width: '70%', paddingRight: '5%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Title (Mandatory) */}
                  <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input
                      type="text"
                      id="title"
                      className="form-control"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      required
                    />
                  </div>

                  {/* Slug (Mandatory) */}
                  <div className="mb-3">
                    <label htmlFor="slug" className="form-label">Slug</label>
                    <input
                      type="text"
                      id="slug"
                      className="form-control"
                      value={slug}
                      onChange={(e) => setSlug(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dropdown Button for Adding Elements */}
                  <div className="btn-group me-1 mt-1">
                    <button
                      type="button"
                      className="btn btn-success dropdown-toggle"
                      data-bs-toggle="dropdown"
                      aria-haspopup="true"
                      aria-expanded="false"
                    >
                      Add Element <i className="mdi mdi-chevron-down"></i>
                    </button>
                    <div className="dropdown-menu">
                      <a className="dropdown-item" href="#" onClick={handleAddTitle}>
                        Add Title
                      </a>
                      <a className="dropdown-item" href="#" onClick={handleAddDescription}>
                        Add Description
                      </a>
                      <a className="dropdown-item" href="#" onClick={handleAddLink}>
                        Add Link
                      </a>
                      <a className="dropdown-item" href="#" onClick={handleAddImage}>
                        Add Image
                      </a>
                    </div>
                  </div>

                  {/* Titles, Descriptions, Links, and Images */}
                  {/* Render dynamically based on the state */}
                  {/* The rest of the form elements remain unchanged */}
                  <button type="submit" className="btn btn-success">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Mobile Frame */}
          <div style={{ width: '30%', position: 'sticky', top: '20px' }}>
            <div
              style={{
                width: '100%',
                height: '667px',
                maxWidth: '375px',
                margin: 'auto',
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                backgroundColor: '#fff',
                overflow: 'auto',
              }}
            >
              <div
                id="mobile-frame"
                dangerouslySetInnerHTML={{ __html: htmlContent }}  // Render the HTML content
              ></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
