import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import './styles.css';

const App = () => {
  // State for the page content
  const [pageComponents, setPageComponents] = useState([]);

  // Function to handle delete action
  const deleteComponent = (id) => {
    setPageComponents((prevComponents) => prevComponents.filter((comp) => comp.id !== id));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="app">
        <Sidebar />
        <MainContent
          pageComponents={pageComponents}
          setPageComponents={setPageComponents}
          deleteComponent={deleteComponent}
        />
      </div>
    </DndProvider>
  );
};

// Sidebar component with draggable items
const Sidebar = () => {
  const draggableComponents = [
    { id: 'text1', type: 'TEXT', content: 'This is a text block.' },
    { id: 'text2', type: 'TEXT', content: 'Another text block.' },
    { id: 'button', type: 'BUTTON', content: 'Click Me' },
  ];

  return (
    <div className="sidebar">
      <h2>Sidebar</h2>
      {draggableComponents.map((item) => (
        <DraggableComponent key={item.id} item={item} />
      ))}
    </div>
  );
};

// Draggable component for sidebar items
const DraggableComponent = ({ item }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: item.type,
    item,
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      className="draggable-item"
      style={{ opacity: isDragging ? 0.5 : 1 }}
    >
      {item.content}
    </div>
  );
};

// Main content area where items will be dropped
const MainContent = ({ pageComponents, setPageComponents, deleteComponent }) => {
  const [{ canDrop, isOver }, drop] = useDrop(() => ({
    accept: 'TEXT',
    drop: (item) => addComponent(item),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }));

  const addComponent = (item) => {
    setPageComponents((prev) => [
      ...prev,
      { ...item, id: `${item.id}-${Date.now()}` },
    ]);
  };

  return (
    <div ref={drop} className="main-content">
      <h2>Main Content</h2>
      {pageComponents.map((component) => (
        <Component key={component.id} component={component} deleteComponent={deleteComponent} />
      ))}
    </div>
  );
};

// Component to render dropped items and provide a delete button
const Component = ({ component, deleteComponent }) => {
  return (
    <div className="component">
      {component.type === 'TEXT' && <p>{component.content}</p>}
      {component.type === 'BUTTON' && <button>{component.content}</button>}
      <button className="delete-btn" onClick={() => deleteComponent(component.id)}>
        Delete
      </button>
    </div>
  );
};

export default App;
