import React, { useState, useEffect } from 'react';

const Slotmanagement = () => {
  const [qrId, setQrId] = useState(''); // State to store selected QR ID
  const [date, setDate] = useState(''); // State for date
  const [slotTime, setSlotTime] = useState(''); // State for slot time
  const [endTime, setEndTime] = useState(''); // State for end time
  const [redirectUrl, setRedirectUrl] = useState(''); // State for redirect URL
  const [brandNames, setBrandNames] = useState([]); // State for storing fetched brand names
  const [qrIds, setQrIds] = useState([]); // State for storing fetched QR IDs
  const [selectedBrandName, setSelectedBrandName] = useState(''); // State for selected brand name

  // Fetch QR IDs from backend
  useEffect(() => {
    const fetchQrIds = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/get_qrs'); // API endpoint for QR IDs
        const data = await response.json();

        // Check if the response contains the Qrs array
        if (data.success && Array.isArray(data.Qrs)) {
          setQrIds(data.Qrs); // Set qrIds state with the correct array from the 'Qrs' key
        } else {
          console.error('Invalid response format for QR IDs:', data);
        }
      } catch (error) {
        console.error('Error fetching QR IDs:', error);
      }
    };

    fetchQrIds();
  }, []);

  // Fetch brand names from backend
  useEffect(() => {
    const fetchBrandNames = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/get_brand');
        const data = await response.json();
        console.log('Fetched Brands:', data); // Debugging line

        // Change 'brands' to 'Brands' (match the casing as per the response structure)
        if (data.success && Array.isArray(data.Brands)) {
          setBrandNames(data.Brands); // Set brand names if the response structure is correct
        } else {
          console.error("Error: Brands data is not in expected array format.");
        }
      } catch (error) {
        console.error('Error fetching brand names:', error);
      }
    };

    fetchBrandNames();
  }, []);



  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    // Prepare the data to send to the backend in the desired format
    const formData = {
      date: new Date(date).toISOString(),  // Convert to ISO string
      startTime: slotTime,                 // Slot start time (12-hour format)
      endTime: endTime,                    // Slot end time (12-hour format)
      qrId,                                // The selected QR ID
      brandId: selectedBrandName,          // Brand ID (Assumed to be selected from your form)
      redirectionUrl: redirectUrl         // Redirect URL
    };
  console.log(formData)
    try {
      // Send POST request to the backend to save the data
      const response = await fetch('http://localhost:7000/api/add_slot', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData), // Convert data to JSON format
      });

      const data = await response.json(); // Parse the response from the backend

      if (data.success) {
        // If the data was saved successfully
        console.log('Slot successfully added!', data);
        alert('Slot saved successfully!');
      } else {
        // Handle error from backend
        console.error('Failed to add slot:', data.message);
        alert('Error saving slot. Please try again!');
      }
    } catch (error) {
      // Handle network or other errors
      console.error('Error submitting the form:', error);
      alert('Network error. Please try again!');
    }
  };



  return (
    <div className="page-content">
      {/* start page title */}
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>QR Code Selection</h4>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* end page title */}

      <div className="container-fluid">
        <div className="page-content-wrapper">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <form onSubmit={handleSubmit}>
                    {/* QR ID Dropdown */}
                    <div className="mb-3">
                      <label htmlFor="qrId" className="form-label">QR Slot Booking Form</label>
                      <select
                        id="qrId"
                        className="form-select"
                        value={qrId}
                        onChange={(e) => setQrId(e.target.value)}
                        required
                      >
                        <option value="">Select QR ID</option>
                        {qrIds.map((qr) => (
                          <option key={qr._id} value={qr.qrCodeId}>{qr.qrCodeId}</option>
                        ))}
                      </select>
                    </div>

                    {/* Brand Name Dropdown */}
                    <div className="mb-3">
                      <label htmlFor="brandName" className="form-label">Select Brand Name</label>
                      <select
  id="brandName"
  className="form-select"
  value={selectedBrandName}
  onChange={(e) => setSelectedBrandName(e.target.value)}
  required
>
  <option value="">Select Brand Name</option>
  {brandNames.length > 0 ? (
    brandNames.map((brand) => (
      <option key={brand._id} value={brand._id}> {/* Corrected: Send brandId, not brandName */}
        {brand.brandName}
      </option>
    ))
  ) : (
    <option disabled>No brands available</option>
  )}
</select>

                    </div>

                    {/* Date Input */}
                    <div className="mb-3">
                      <label htmlFor="date" className="form-label">Select Date</label>
                      <input
                        type="date"
                        id="date"
                        className="form-control"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        required
                      />
                    </div>

                    {/* Slot Time Input (Time Only) */}
                    <div className="mb-3">
                      <label htmlFor="slotTime" className="form-label">Select Slot Time</label>
                      <input
                        type="time"
                        id="slotTime"
                        className="form-control"
                        value={slotTime}
                        onChange={(e) => setSlotTime(e.target.value)}
                        required
                      />
                    </div>

                    {/* End Time Input (Time Only) */}
                    <div className="mb-3">
                      <label htmlFor="endTime" className="form-label">Select End Time</label>
                      <input
                        type="time"
                        id="endTime"
                        className="form-control"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                        required
                      />
                    </div>

                    {/* Redirect URL Input */}
                    <div className="mb-3">
                      <label htmlFor="redirectUrl" className="form-label">Redirect URL</label>
                      <input
                        type="url"
                        id="redirectUrl"
                        className="form-control"
                        value={redirectUrl}
                        onChange={(e) => setRedirectUrl(e.target.value)}
                        required
                      />
                    </div>

                    <button type="submit" className="btn btn-primary">Submit</button>
                  </form>
                </div>
              </div>
            </div> {/* end col */}
          </div> {/* end row */}
        </div>
      </div> {/* container */}
    </div>
  );
};

export default Slotmanagement;
