import { useState } from 'react';
import { QRCodeCanvas } from 'qrcode.react'; // Import the QR code library

function QRCodeDesign() {
  const [qrData, setQrData] = useState('');  // QR Code Data
  const [selectedTemplate, setSelectedTemplate] = useState('classic');  // Template selection
  const [qrCodeUrl, setQrCodeUrl] = useState('');  // For storing generated QR code URL
  const [errorMessage, setErrorMessage] = useState('');  // For error messages

  // Template preview images (replace with actual image URLs)
  const templates = {
    classic: "https://example.com/classic-template.png",  // Replace with actual image URL or path
    blue: "https://example.com/blue-template.png",      // Replace with actual image URL or path
    green: "https://example.com/green-template.png",    // Replace with actual image URL or path
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      if (!qrData) {
        setErrorMessage("Please enter QR code data");
        return;
      }

      // Create QR code based on the data and selected template
      setErrorMessage('');
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');

      // Generate the QR code using the selected template
      const qrCodeElement = QRCode({
        value: qrData,
        size: 256,
        renderers: 'canvas',  // Rendering method
      });

      // Set the canvas context with the QR code data
      context.drawImage(qrCodeElement, 0, 0);

      // Set the URL of the generated QR code image
      setQrCodeUrl(canvas.toDataURL());
    } catch (error) {
      setErrorMessage('Failed to generate QR code. Please try again.');
    }
  };

  return (
    <>
      <div className="page-content">
        <div className="page-title-box">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col-sm-6">
                <div className="page-title">
                  <h4>Generate QR Code</h4>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="container-fluid">
          <div className="page-content-wrapper">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-body">
                    <h4 className="header-title">QR Code Information</h4>
                    <p className="card-title-desc">Enter data to generate a QR code</p>

                    {/* Error Message */}
                    {errorMessage && (
                      <div className="alert alert-danger" role="alert">
                        {errorMessage}
                      </div>
                    )}

                    <form onSubmit={handleSubmit}>
                      {/* QR Code Data */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="qrData">QR Code Data</label>
                        <input
                          id="qrData"
                          name="qrData"
                          type="text"
                          className="form-control"
                          placeholder="Enter data for QR code"
                          value={qrData}
                          onChange={(e) => setQrData(e.target.value)}
                          required
                        />
                      </div>

                      {/* Template Selection */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="template">Select QR Code Design</label>
                        <select
                          id="template"
                          name="template"
                          className="form-control"
                          value={selectedTemplate}
                          onChange={(e) => setSelectedTemplate(e.target.value)}
                        >
                          <option value="classic">Classic</option>
                          <option value="blue">Blue</option>
                          <option value="green">Green</option>
                        </select>
                      </div>

                      {/* Template Preview */}
                      <div className="template-preview">
                        <h5>QR Code Template Preview</h5>
                        <div className="template-images">
                          {Object.keys(templates).map((template) => (
                            <div key={template} className="template-option">
                              <input
                                type="radio"
                                id={template}
                                name="template"
                                value={template}
                                checked={selectedTemplate === template}
                                onChange={() => setSelectedTemplate(template)}
                              />
                              <label htmlFor={template}>
                                <img
                                  src={templates[template]}
                                  alt={`${template} template`}
                                  style={{ width: '100px', height: '100px', margin: '10px' }}
                                />
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>

                      <button type="submit" className="btn btn-primary">Generate QR Code</button>
                    </form>
                  </div>
                </div>
              </div>
            </div>

            {/* QR Code Preview */}
            {qrCodeUrl && (
              <div className="mt-4">
                <h4>Generated QR Code</h4>
                <img src={qrCodeUrl} alt="Generated QR Code" style={{ width: '200px', height: '200px' }} />
                <a href={qrCodeUrl} download="generated-qr-code.png">
                  <button className="btn btn-success mt-2">Download QR Code</button>
                </a>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default QRCodeDesign;
