import React from 'react';

function LandingPage1() {
    return (
        <div>
            {/* Hero Section */}
            <section style={{
                background: 'url("{{heroBackgroundUrl}}") center/cover no-repeat',
                color: '#fff',
                textAlign: 'center',
                padding: '50px 20px',
                backgroundSize: 'cover',
                height: '100vh',
            }}>
                <h1 style={{
                    fontSize: '36px',
                    margin: '0',
                    fontWeight: 'bold',
                    lineHeight: '1.4',
                }}>
                    Discover {'{{productName}}'}
                </h1>
                <p style={{
                    fontSize: '18px',
                    margin: '10px 0 20px',
                }}>
                    Scan the QR code to get detailed information about our product
                </p>
                <a href="#details" style={{
                    textDecoration: 'none',
                    backgroundColor: '#0073e6',
                    padding: '12px 25px',
                    color: '#fff',
                    borderRadius: '5px',
                    fontSize: '16px',
                    display: 'inline-block',
                }}>
                    {'{{heroButtonText}}'}
                </a>
            </section>

            {/* Products Section */}
            <section id="products" style={{
                padding: '30px 20px',
                backgroundColor: '#fff',
                textAlign: 'center',
            }}>
                <h2 style={{
                    fontSize: '28px',
                    marginBottom: '20px',
                }}>
                    Our Products
                </h2>
                <p style={{
                    fontSize: '16px',
                    marginBottom: '30px',
                }}>
                    Explore our latest products below and enjoy exclusive discounts with coupon codes!
                </p>

                {/* Product Items */}
                {'{{#each products}}'}
                <div style={{
                    maxWidth: '100%',
                    margin: '0 auto 40px',
                    backgroundColor: '#f4f4f4',
                    padding: '20px',
                    borderRadius: '10px',
                    textAlign: 'left',
                    boxSizing: 'border-box',
                }}>
                    <img src="{{this.imageUrl}}" alt="{{this.name}}" style={{
                        width: '100%',
                        height: 'auto',
                        borderRadius: '10px',
                    }} />
                    <h3 style={{
                        fontSize: '20px',
                        margin: '20px 0 10px',
                    }}>
                        {'{{this.name}}'}
                    </h3>
                    <p style={{
                        fontSize: '14px',
                        color: '#555',
                    }}>
                        {'{{this.description}}'}
                    </p>
                    <p style={{
                        fontSize: '14px',
                        fontWeight: 'bold',
                    }}>
                        Use code: <span style={{
                            backgroundColor: '#0073e6',
                            color: '#fff',
                            padding: '5px 10px',
                            borderRadius: '5px',
                        }}>
                            {'{{this.couponCode}}'}
                        </span> for {'{{this.discount}}'}
                    </p>
                </div>
                {'{{/each}}'}
            </section>

            {/* QR Code Section */}
            <section id="details" style={{
                padding: '30px 20px',
                backgroundColor: '#fff',
                textAlign: 'center',
            }}>
                <h2 style={{
                    fontSize: '28px',
                    marginBottom: '20px',
                }}>
                    Product Information
                </h2>
                <p style={{
                    fontSize: '16px',
                    marginBottom: '20px',
                }}>
                    Scan the QR code below to instantly access detailed product information, specifications, and benefits.
                </p>
                <div style={{
                    display: 'inline-block',
                    padding: '20px',
                    backgroundColor: '#f4f4f4',
                    borderRadius: '10px',
                    marginBottom: '20px',
                }}>
                    <img src="{{qrCodeUrl}}" alt="QR Code for Product Information" style={{
                        width: '200px',
                        height: '200px',
                    }} />
                </div>
                <p style={{
                    marginTop: '20px',
                    fontSize: '16px',
                    color: '#555',
                }}>
                    * Use your smartphone's camera to scan the code and learn more.
                </p>
            </section>
        </div>
    );
}

export default LandingPage1;
