import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import QREditor from './QREditor'; // Import the QREditor component

function QRManagement() {
  const [qrList, setQrList] = useState([]);
  const [showEditor, setShowEditor] = useState(null); // Track the QR being edited
  const navigate = useNavigate();

  // Fetch QR codes from the backend
  useEffect(() => {
    const fetchQRCodes = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/get_qrs');
        setQrList(response.data.Qrs);
      } catch (error) {
        console.error('Error fetching QR codes:', error);
      }
    };
    fetchQRCodes();
  }, []);

  // Toggle editor for a specific QR
  const toggleEditor = (qrId) => {
    setShowEditor(showEditor === qrId ? null : qrId); // Toggle QR editor visibility
  };

  return (
    <div className="page-content">
      <div className="container-fluid">
        <div className="page-content-wrapper">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <div className="p-4" style={{ minHeight: 300 }}>
                    <h5>QR Code Management</h5>

                    {/* Render QR Codes */}
                    <div className="row mt-4">
                      {qrList.map((qr) => {
                        const qrUrl = `https://qrbackend-aio3.onrender.com/api/redirect/${qr.qrCodeId}`;
                        return (
                          <div key={qr.qrCodeId} className="col-md-4 text-center mb-3">
                            {/* Display QR Code */}
                            <QRCodeCanvas
                              value={qrUrl}
                              size={150}
                              bgColor="#ffffff"
                              fgColor="#000000"
                              level={"H"}
                            />
                            <p className="mt-2">QR ID: {qr.qrCodeId}</p>
                            <button
                              className="btn btn-primary mt-2"
                              onClick={() => toggleEditor(qr.qrCodeId)}
                            >
                              Edit QR
                            </button>

                            {/* Show QR Editor */}
                            {showEditor === qr.qrCodeId && (
                              <QREditor qrCodeUrl={qrUrl} qrId={qr.qrCodeId} />
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            </div> {/* end col */}
          </div> {/* end row */}
        </div>
      </div> {/* container-fluid */}
    </div>
  );
}

export default QRManagement;
