import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import './styles.css';

const App = () => {
  const [pageComponents, setPageComponents] = useState([]);
  const [clickedPosition, setClickedPosition] = useState({ x: 0, y: 0 });

  // Handle adding components by clicking inside the mobile frame
  const handleFrameClick = (e) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setClickedPosition({ x, y });
  };

  // Function to delete components
  const deleteComponent = (id) => {
    setPageComponents((prevComponents) => prevComponents.filter((comp) => comp.id !== id));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="app">
        <Sidebar addComponent={addComponent} />
        <MainContent
          pageComponents={pageComponents}
          setPageComponents={setPageComponents}
          deleteComponent={deleteComponent}
          handleFrameClick={handleFrameClick}
        />
      </div>
    </DndProvider>
  );

  // Add component to page when clicked in sidebar
  function addComponent(item) {
    setPageComponents((prevComponents) => [
      ...prevComponents,
      { ...item, id: `${item.id}-${Date.now()}`, x: clickedPosition.x, y: clickedPosition.y },
    ]);
  }
};

// Sidebar with clickable items
const Sidebar = ({ addComponent }) => {
  const components = [
    { id: 'text1', type: 'TEXT', content: 'This is a text block.' },
    { id: 'button', type: 'BUTTON', content: 'Click Me' },
    { id: 'link', type: 'LINK', content: 'Visit Levi\'s', url: 'https://www.levi.com' },
    { id: 'image', type: 'IMAGE', content: 'Image of Jeans', url: 'https://via.placeholder.com/150' },
    { id: 'video', type: 'VIDEO', content: 'Product Video', url: 'https://www.youtube.com/embed/dQw4w9WgXcQ' },
  ];

  return (
    <div className="sidebar">
      <h2>Sidebar</h2>
      {components.map((item) => (
        <div
          key={item.id}
          className="draggable-item"
          onClick={() => addComponent(item)}
        >
          {item.content}
        </div>
      ))}
    </div>
  );
};

// Main content area where items will be dropped
const MainContent = ({ pageComponents, deleteComponent, handleFrameClick }) => {
  return (
    <div className="main-content">
      <h2>Main Content</h2>
      {/* Mobile Frame */}
      <div className="mobile-frame" onClick={handleFrameClick}>
        <div className="mobile-content">
          {pageComponents.map((component) => (
            <Component
              key={component.id}
              component={component}
              deleteComponent={deleteComponent}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

// Component to render dropped items
const Component = ({ component, deleteComponent }) => {
  return (
    <div className="component" style={{ left: component.x, top: component.y }}>
      {component.type === 'TEXT' && <p>{component.content}</p>}
      {component.type === 'BUTTON' && <button>{component.content}</button>}
      {component.type === 'LINK' && <a href={component.url} target="_blank" rel="noopener noreferrer">{component.content}</a>}
      {component.type === 'IMAGE' && <img src={component.url} alt={component.content} />}
      {component.type === 'VIDEO' && <iframe src={component.url} title={component.content} width="100%" height="200" />}
      <button className="delete-btn" onClick={() => deleteComponent(component.id)}>
        Delete
      </button>
    </div>
  );
};

export default App;
