import React, { useState } from 'react';

const EditTemplate = () => {
  // Initial state for form fields
  const [headings, setHeadings] = useState([
    { title: '', description: '', link: '', image: null }
  ]);
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Handle changes to the form fields
  const handleHeadingChange = (index, e) => {
    const { name, value } = e.target;
    const newHeadings = [...headings];
    newHeadings[index][name] = value;
    setHeadings(newHeadings);
  };

  // Handle image change
  const handleImageChange = (index, e) => {
    const newHeadings = [...headings];
    newHeadings[index].image = e.target.files[0];
    setHeadings(newHeadings);
  };

  // Add a new heading section
  const handleAddHeading = () => {
    setHeadings([...headings, { title: '', description: '', link: '', image: null }]);
  };

  // Remove a heading section
  const handleRemoveHeading = (index) => {
    const newHeadings = headings.filter((_, i) => i !== index);
    setHeadings(newHeadings);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);

    // Add all headings, descriptions, links, and images to formData
    headings.forEach((heading, index) => {
      formData.append(`title${index + 1}`, heading.title);
      formData.append(`description${index + 1}`, heading.description);
      formData.append(`link${index + 1}`, heading.link);
      formData.append(`image${index + 1}`, heading.image);
    });

    // Submit the form data
    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  return (
    <div className="page-content">
      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Upload Logo */}
                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Brand Description */}
                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dynamic Headings */}
                  {headings.map((heading, index) => (
                    <div key={index} className="mb-3">
                      {/* Title */}
                      <div className="mb-2">
                        <label htmlFor={`title${index}`} className="form-label">Title {index + 1}</label>
                        <input
                          type="text"
                          name="title"
                          id={`title${index}`}
                          className="form-control"
                          value={heading.title}
                          onChange={(e) => handleHeadingChange(index, e)}
                          required
                        />
                      </div>

                      {/* Description */}
                      <div className="mb-2">
                        <label htmlFor={`description${index}`} className="form-label">Description {index + 1}</label>
                        <textarea
                          name="description"
                          id={`description${index}`}
                          className="form-control"
                          rows="3"
                          value={heading.description}
                          onChange={(e) => handleHeadingChange(index, e)}
                          required
                        />
                      </div>

                      {/* Link */}
                      <div className="mb-2">
                        <label htmlFor={`link${index}`} className="form-label">Link {index + 1}</label>
                        <input
                          type="url"
                          name="link"
                          id={`link${index}`}
                          className="form-control"
                          value={heading.link}
                          onChange={(e) => handleHeadingChange(index, e)}
                        />
                      </div>

                      {/* Image Upload */}
                      <div className="mb-2">
                        <label htmlFor={`image${index}`} className="form-label">Upload Image {index + 1}</label>
                        <input
                          type="file"
                          name="image"
                          id={`image${index}`}
                          className="form-control"
                          onChange={(e) => handleImageChange(index, e)}
                        />
                      </div>

                      {/* Remove Heading Button */}
                      {headings.length > 1 && (
                        <button
                          type="button"
                          className="btn btn-danger"
                          onClick={() => handleRemoveHeading(index)}
                        >
                          Remove Heading {index + 1}
                        </button>
                      )}
                    </div>
                  ))}

                  {/* Add New Heading Button */}
                  <button
                    type="button"
                    onClick={handleAddHeading}
                    className="btn btn-success mb-3"
                  >
                    Add New Heading
                  </button>

                  {/* Success Message */}
                  {showSuccessMessage && (
                    <div className="alert alert-success mb-3" role="alert">
                      Data successfully added!
                    </div>
                  )}

                  <button type="submit" className="btn btn-primary">Submit</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
