import { useState } from 'react';
import { QRCodeCanvas } from 'qrcode.react';  // We are using QRCodeCanvas to render the QR code

function QRCodeDesign() {
  const [qrData, setQrData] = useState('');  // QR Code Data
  const [selectedTemplate, setSelectedTemplate] = useState('classic');  // Template selection
  const [errorMessage, setErrorMessage] = useState('');  // For error messages

  // Define styles for different templates
  const templates = {
    classic: {
      bgColor: 'white',
      fgColor: 'black',
    },
    blue: {
      bgColor: 'lightblue',
      fgColor: 'darkblue',
    },
    green: {
      bgColor: 'lightgreen',
      fgColor: 'darkgreen',
    },
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!qrData) {
      setErrorMessage("Please enter QR code data");
      return;
    }
    setErrorMessage('');  // Clear error if QR data is present
  };

  return (
    <>
      <div className="page-content">
        <div className="page-title-box">
          <div className="container-fluid">
            <div className="row align-items-center">
              <div className="col-sm-6">
                <div className="page-title">
                  <h4>Generate QR Code</h4>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="container-fluid">
          <div className="page-content-wrapper">
            <div className="row">
              <div className="col-lg-12">
                <div className="card">
                  <div className="card-body">
                    <h4 className="header-title">QR Code Information</h4>
                    <p className="card-title-desc">Enter data to generate a QR code</p>

                    {/* Error Message */}
                    {errorMessage && (
                      <div className="alert alert-danger" role="alert">
                        {errorMessage}
                      </div>
                    )}

                    <form onSubmit={handleSubmit}>
                      {/* QR Code Data */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="qrData">QR Code Data</label>
                        <input
                          id="qrData"
                          name="qrData"
                          type="text"
                          className="form-control"
                          placeholder="Enter data for QR code"
                          value={qrData}
                          onChange={(e) => setQrData(e.target.value)}
                          required
                        />
                      </div>

                      {/* Template Selection */}
                      <div className="mb-3">
                        <label className="form-label" htmlFor="template">Select QR Code Design</label>
                        <select
                          id="template"
                          name="template"
                          className="form-control"
                          value={selectedTemplate}
                          onChange={(e) => setSelectedTemplate(e.target.value)}
                        >
                          <option value="classic">Classic</option>
                          <option value="blue">Blue</option>
                          <option value="green">Green</option>
                        </select>
                      </div>

                      {/* Template Preview */}
                      <div className="template-preview">
                        <h5>QR Code Template Preview</h5>
                        <div className="template-images">
                          {Object.keys(templates).map((template) => (
                            <div key={template} className="template-option">
                              <input
                                type="radio"
                                id={template}
                                name="template"
                                value={template}
                                checked={selectedTemplate === template}
                                onChange={() => setSelectedTemplate(template)}
                              />
                              <label htmlFor={template}>
                                <div
                                  style={{
                                    width: '100px',
                                    height: '100px',
                                    backgroundColor: templates[template].bgColor,
                                    border: '2px solid #ccc',
                                    margin: '10px',
                                  }}
                                >
                                  {/* Display the template name */}
                                  <p
                                    style={{
                                      textAlign: 'center',
                                      color: templates[template].fgColor,
                                      fontWeight: 'bold',
                                    }}
                                  >
                                    {template}
                                  </p>
                                </div>
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>

                      <button type="submit" className="btn btn-primary">Generate QR Code</button>
                    </form>
                  </div>
                </div>
              </div>
            </div>

            {/* QR Code Preview */}
            {qrData && (
              <div className="mt-4">
                <h4>Generated QR Code</h4>
                <QRCodeCanvas
                  value={qrData}
                  size={200}
                  bgColor={templates[selectedTemplate].bgColor}  // Apply the selected template's background color
                  fgColor={templates[selectedTemplate].fgColor}  // Apply the selected template's foreground color
                  level="L"
                />
                <a href={qrData} download="generated-qr-code.png">
                  <button className="btn btn-success mt-2">Download QR Code</button>
                </a>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default QRCodeDesign;
