import React, { useState, useEffect } from 'react';

const EditTemplate = () => {
  const [fileUrl, setFileUrl] = useState(''); // State to store the file URL
  const [logo, setLogo] = useState(null);
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [dynamicSections, setDynamicSections] = useState([{
    title: '',
    description: '',
    link: '',
    image: null
  }]); // State to store dynamic sections (image, text, link)

  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // Fetch the file URL from the backend API
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId'); // Get templateId from URL
    if (templateId) {
      const fetchFileUrl = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-file/${templateId}`);
          const data = await response.json();
          if (data.fileUrl) {
            setFileUrl(data.fileUrl); // Set the file URL state
          }
        } catch (error) {
          console.error('Error fetching file URL:', error);
        }
      };
      fetchFileUrl();
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('logo', logo);
    formData.append('brandName', brandName);
    formData.append('brandDescription', brandDescription);

    // Append dynamic sections data
    dynamicSections.forEach((section, index) => {
      formData.append(`section[${index}][title]`, section.title);
      formData.append(`section[${index}][description]`, section.description);
      formData.append(`section[${index}][link]`, section.link);
      if (section.image) {
        formData.append(`section[${index}][image]`, section.image);
      }
    });

    try {
      const response = await fetch('http://localhost:7000/api/add_brand_info', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setShowSuccessMessage(true);
      } else {
        alert('Error saving data. Please try again!');
      }
    } catch (error) {
      alert('Network error. Please try again!');
    }
  };

  const handleAddSection = () => {
    setDynamicSections([
      ...dynamicSections,
      { title: '', description: '', link: '', image: null }
    ]);
  };

  const handleRemoveSection = (index) => {
    const newSections = dynamicSections.filter((_, idx) => idx !== index);
    setDynamicSections(newSections);
  };

  const handleChangeSection = (index, field, value) => {
    const newSections = [...dynamicSections];
    newSections[index][field] = value;
    setDynamicSections(newSections);
  };

  const handleImageChange = (index, file) => {
    const newSections = [...dynamicSections];
    newSections[index].image = file;
    setDynamicSections(newSections);
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex' }}>
          {/* Left Side: Form */}
          <div style={{ width: '60%', paddingRight: '10%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  {/* Upload Logo */}
                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Upload Logo</label>
                    <input
                      type="file"
                      id="logo"
                      className="form-control"
                      onChange={(e) => setLogo(e.target.files[0])}
                      required
                    />
                  </div>

                  {/* Brand Name */}
                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  {/* Brand Description */}
                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {/* Dynamic Sections: Image, Text, Link */}
                  {dynamicSections.map((section, index) => (
                    <div key={index} className="dynamic-section mb-3">
                      <h5>Section {index + 1}</h5>

                      {/* Title */}
                      <div className="mb-2">
                        <label htmlFor={`title-${index}`} className="form-label">Title</label>
                        <input
                          type="text"
                          id={`title-${index}`}
                          className="form-control"
                          value={section.title}
                          onChange={(e) => handleChangeSection(index, 'title', e.target.value)}
                          required
                        />
                      </div>

                      {/* Description */}
                      <div className="mb-2">
                        <label htmlFor={`description-${index}`} className="form-label">Description</label>
                        <textarea
                          id={`description-${index}`}
                          className="form-control"
                          rows="3"
                          value={section.description}
                          onChange={(e) => handleChangeSection(index, 'description', e.target.value)}
                          required
                        />
                      </div>

                      {/* Link */}
                      <div className="mb-2">
                        <label htmlFor={`link-${index}`} className="form-label">Link</label>
                        <input
                          type="url"
                          id={`link-${index}`}
                          className="form-control"
                          value={section.link}
                          onChange={(e) => handleChangeSection(index, 'link', e.target.value)}
                          required
                        />
                      </div>

                      {/* Image Upload */}
                      <div className="mb-2">
                        <label htmlFor={`image-${index}`} className="form-label">Upload Image</label>
                        <input
                          type="file"
                          id={`image-${index}`}
                          className="form-control"
                          onChange={(e) => handleImageChange(index, e.target.files[0])}
                        />
                      </div>

                      {/* Remove Section Button */}
                      <button
                        type="button"
                        className="btn btn-danger"
                        onClick={() => handleRemoveSection(index)}
                      >
                        Remove Section
                      </button>
                    </div>
                  ))}

                  {/* Add New Section Button */}
                  <button
                    type="button"
                    className="btn btn-success mb-3"
                    onClick={handleAddSection}
                  >
                    Add New Section
                  </button>

                  {/* Success Message */}
                  {showSuccessMessage && (
                    <div className="alert alert-success mb-3" role="alert">
                      Data successfully added!
                    </div>
                  )}

                  <button type="submit" className="btn btn-primary">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Sticky Mobile Frame with HTML Content */}
          <div
            style={{
              width: '30%',
              position: 'sticky',
              top: '20px', // Keep it 20px from the top
              height: 'auto',
              maxHeight: '90vh',
            }}
          >
            <div
              style={{
                position: 'sticky',
                top: '20px',
                width: '350px', // Adjusted width for the mobile frame
                height: '650px', // Adjusted height
                border: '16px solid #e7e7e7',
                borderRadius: '20px',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {fileUrl && (
                <iframe
                  src={fileUrl}
                  width="100%"
                  height="100%"
                  style={{
                    border: 'none',
                    borderRadius: '20px',
                  }}
                  title="Mobile Template"
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditTemplate;
