import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SlotUpdate({ slotId }) {
  const [slot, setSlot] = useState({
    date: '',
    startTime: '',
    endTime: '',
    qrId: '',
    brandId: '',
    redirectionUrl: '',
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch the current slot details
    axios
      .get(`http://localhost:7000/api/get_slot/${slotId}`)
      .then((response) => {
        setSlot(response.data.Slot);
      })
      .catch((err) => {
        console.error("Error fetching the slot data", err);
        setError("Error fetching slot details.");
      });
  }, [slotId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSlot((prevSlot) => ({
      ...prevSlot,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setIsLoading(true);

    // Send updated data to backend
    axios
      .put(`http://localhost:7000/api/update_slot/${slotId}`, slot)
      .then((response) => {
        setIsLoading(false);
        alert("Slot updated successfully!");
      })
      .catch((err) => {
        setIsLoading(false);
        setError("Error updating the slot.");
        console.error(err);
      });
  };

  return (
    <div className="slot-update">
      <h3>Update Slot</h3>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            name="date"
            value={slot.date}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Start Time:</label>
          <input
            type="time"
            name="startTime"
            value={slot.startTime}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>End Time:</label>
          <input
            type="time"
            name="endTime"
            value={slot.endTime}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>QR ID:</label>
          <input
            type="text"
            name="qrId"
            value={slot.qrId}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Brand ID:</label>
          <input
            type="text"
            name="brandId"
            value={slot.brandId}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Redirection URL:</label>
          <input
            type="url"
            name="redirectionUrl"
            value={slot.redirectionUrl}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <button type="submit" disabled={isLoading}>
            {isLoading ? "Updating..." : "Update Slot"}
          </button>
        </div>
      </form>
    </div>
  );
}

export default SlotUpdate;
