import React, { useState, useEffect } from 'react';

const Edit = () => {
  const [fileUrl, setFileUrl] = useState('');
  const [htmlContent, setHtmlContent] = useState('');
  const [brandName, setBrandName] = useState('');
  const [brandDescription, setBrandDescription] = useState('');
  const [titles, setTitles] = useState([]);
  const [descriptions, setDescriptions] = useState([]);
  const [links, setLinks] = useState([]);
  const [images, setImages] = useState([]);

  // Fetch HTML content
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const templateId = urlParams.get('templateId');

    if (templateId) {
      const fetchHtmlContent = async () => {
        try {
          const response = await fetch(`http://localhost:7000/api/get-html/${templateId}`);
          if (response.ok) {
            const html = await response.text();
            setHtmlContent(html);
          } else {
            console.error('Error fetching HTML content:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching HTML content:', error);
        }
      };

      fetchHtmlContent();
    }
  }, []);

  // Form Handlers
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      brandName,
      brandDescription,
      titles,
      descriptions,
      links,
      images,
    });
  };

  const handleAddTitle = () => setTitles([...titles, '']);
  const handleAddDescription = () => setDescriptions([...descriptions, '']);
  const handleAddLink = () => setLinks([...links, '']);
  const handleAddImage = () => setImages([...images, null]);

  const handleChange = (index, type, value) => {
    switch (type) {
      case 'title':
        setTitles(titles.map((t, i) => (i === index ? value : t)));
        break;
      case 'description':
        setDescriptions(descriptions.map((d, i) => (i === index ? value : d)));
        break;
      case 'link':
        setLinks(links.map((l, i) => (i === index ? value : l)));
        break;
      default:
        break;
    }
  };

  const handleImageChange = (index, file) => {
    setImages(images.map((img, i) => (i === index ? file : img)));
  };

  const handleRemove = (index, type) => {
    switch (type) {
      case 'title':
        setTitles(titles.filter((_, i) => i !== index));
        break;
      case 'description':
        setDescriptions(descriptions.filter((_, i) => i !== index));
        break;
      case 'link':
        setLinks(links.filter((_, i) => i !== index));
        break;
      case 'image':
        setImages(images.filter((_, i) => i !== index));
        break;
      default:
        break;
    }
  };

  return (
    <div className="page-content">
      <div className="page-title-box">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-sm-6">
              <div className="page-title">
                <h4>Brand Information Form</h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid">
        <div className="page-content-wrapper" style={{ display: 'flex', gap: '10px' }}>
          {/* Left Side: Form */}
          <div style={{ width: 'calc(70%)', paddingRight: '5%', overflowY: 'auto', maxHeight: '80vh' }}>
            <div className="card">
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="d-flex justify-content-between mb-4">
                    <button type="button" className="btn btn-primary" onClick={handleAddTitle}>
                      Add Title
                    </button>
                    <button type="button" className="btn btn-primary" onClick={handleAddDescription}>
                      Add Description
                    </button>
                    <button type="button" className="btn btn-primary" onClick={handleAddLink}>
                      Add Link
                    </button>
                    <button type="button" className="btn btn-primary" onClick={handleAddImage}>
                      Add Image
                    </button>
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandName" className="form-label">Brand Name</label>
                    <input
                      type="text"
                      id="brandName"
                      className="form-control"
                      value={brandName}
                      onChange={(e) => setBrandName(e.target.value)}
                      required
                    />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="brandDescription" className="form-label">Brand Description</label>
                    <textarea
                      id="brandDescription"
                      className="form-control"
                      rows="3"
                      value={brandDescription}
                      onChange={(e) => setBrandDescription(e.target.value)}
                      required
                    />
                  </div>

                  {titles.map((title, index) => (
                    <div key={index} className="mb-3">
                      <label className="form-label">Title {index + 1}</label>
                      <input
                        type="text"
                        className="form-control"
                        value={title}
                        onChange={(e) => handleChange(index, 'title', e.target.value)}
                      />
                      <button type="button" onClick={() => handleRemove(index, 'title')}>
                        Remove
                      </button>
                    </div>
                  ))}

                  {/* Repeat for descriptions, links, and images */}

                  <button type="submit" className="btn btn-success">Submit</button>
                </form>
              </div>
            </div>
          </div>

          {/* Right Side: Mobile Frame */}
          <div style={{ width: '30%', position: 'sticky', top: '20px' }}>
            <div
              style={{
                width: '100%',
                height: '667px',
                maxWidth: '375px',
                margin: 'auto',
                border: '16px solid #e7e7e7',
                borderRadius: '40px',
                backgroundColor: '#fff',
                overflow: 'hidden',
              }}
            >
              {htmlContent ? (
                <div dangerouslySetInnerHTML={{ __html: htmlContent }} />
              ) : (
                <p>No content to preview.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Edit;
